    font-size: 1.1em;
    background-color: transparent;
    border: 3px solid #333;
    color: #333;
}

.logs-header {
    text-align: center;
    color: #333;
}

table.dialog {
    border-collapse: collapse;
}

table.dialog th {
    font-family: 'Liquid Crystal', 'LAB디지털', sans-serif;;
    font-size: 1.1em;
    border-right: 1px solid #333;
    color: #333;
    padding: 1.1em 1em 0em 1em;
}

table.dialog td {
    font-family: 'SV Basic Manual', 'RIDIBatang', sans-serif;
    font-size: 1em;
    padding: 1.15em 0em 0em 0.8em;
}

table.wiki-content-table {
   border: 3px solid #333;
}

table.wiki-content-table td {
    font-family: 'SV Basic Manual', 'RIDIBatang', sans-serif;
    font-size: 1em;

    border: none;
    min-width: 50px;
    padding: 0.575em 0.4em 0.575em 0.4em;
}

table.dialog, table.wiki-content-table, table p {
    line-height: 1.3;
}

.logs a, table.dialog a, table.wiki-content-table a {
    color: #777 !important;
    text-decoration: underline;
}
