    box-sizing: border-box;
}
.rate-box-with-credit-button,
.page-rate-widget-box span,
div.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    height: 16px;
    font-family: verdana,arial,helvetica,sans-serif;
    font-size: 1em;
}
.page-rate-widget-box .rateup a::before,
.page-rate-widget-box .ratedown a::before,
.page-rate-widget-box .cancel a::before {
    content: "";
}
.page-rate-widget-box .cancel {
    padding: 0;
}
.page-rate-widget-box .cancel a {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    margin: 0 1px;
    box-sizing: border-box;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
.rate-box-with-credit-button .creditButton {
    background-color: unset;
    border: none 0;
    border-radius: 0;
    box-shadow: none;
    margin-left: 0;
}
.rate-box-with-credit-button .creditButton a {
    color: #fff;
}
.modalbox .modalbox-title {
    border-radius: 0;
}

/* Content */
.archived > .colmod-block > ul > li > .colmod-link-top div a::before,
.interview .colmod-link-top a:nth-child(2)::before {
    content: "[";
}
.archived > .colmod-block > ul > li > .colmod-link-top div a::after,
.interview .colmod-link-top a:nth-child(2)::after {
    content: "]";
}
