[[include :scp-wiki:theme:pack-of-peanuts]]

[[module CSS]]
.footnotes-footer{
     display: none;
}
[[/module]]
[[module CSS]]

div.terminal{
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
    /* black shadow at the top of the terminal */
    background-image:
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
    background-position: center;
    display: block;
    /* shadow around the terminal */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
    /* prevents scanline from forming a scrollwheel */
    overflow:hidden;
}
div.terminal blockquote {
    background-color: black;
    border: double 3px #80FF80
}
div.scanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
    /* Safari 4.0 - 8.0 */
    -webkit-animation: scan 12s linear 0s infinite; /* You may need to change this. If the bar goes by too quickly, increase 5s. */
    animation: scan 12s linear 0s infinite; /* Same as above */
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 2em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
}

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* You may need to change this depending on the length of your terminal. If the scanline stops halfway, increase the second number.*/
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* Same as above.*/
}

div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;
}
div.text a:hover::before{
    content: "> ";
}
[[/module]]

[[module CSS]]
div.wilson-border {
    border: 0.063em solid rgb(102, 87, 49);  
    box-shadow: 0em 0.063em 0.125em rgba(0, 0, 0, 0.1);
}

div.wilson-header {
    color: white;
    background: rgb(163, 193, 98);
    padding: 1em;
    font-size: 150%;
    font-weight: bold;
    text-align: center;
}

div.wilson-subtitle {
    color: rgb(102, 87, 49);
    background: rgba(200, 226, 147);
    padding: 0.313em;
    text-align: right;
    font-weight: bold;
}

div.wilson-body {
    color: rgba(102, 87, 49);
    background: rgb(209, 237, 154);
    padding: 1em;
}
@import url('https://fonts.googleapis.com/css2?family=Gloria+Hallelujah&display=swap');
@import url('https://fonts.googleapis.com/css?family=PT+Mono&display=swap');

.page {
    display: block;
    overflow: hidden;
    font-family: 'Gloria Hallelujah', cursive;
    font-style: normal;
    color: black;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
}

.page p {
    line-height: 20px;
    margin: 0;
}

/* Parawatch Forum Pseudo-Blockquotes */
div.parapost {
    background:  black;
    border-width: 2px;
    border-style: solid;
    border-color: #1a1a1a;
    padding: 0 1em;
    margin: 1em 3em;
}

div.parapost.reply1 {margin: 1em 3em 1em 4em;}
div.parapost.reply2 {margin: 1em 3em 1em 5em;}
div.parapost.reply3 {margin: 1em 3em 1em 6em;}

@media (max-width: 479px) {
   div.parapost {margin: 1em 0;}
   div.parapost.reply1 {margin: 1em 0 1em 1em;}
   div.parapost.reply2 {margin: 1em 0 1em 2em;}
   div.parapost.reply3 {margin: 1em 0 1em 3em;}
}

@media (min-width: 480px) and (max-width: 580px) {
   div.parapost {margin: 0.5em;}
   div.parapost.reply1 {margin: 0.5em 0.5em 0.5em 1em;}
   div.parapost.reply2 {margin: 0.5em 0.5em 0.5em 1.5em;}
   div.parapost.reply3 {margin: 0.5em 0.5em 0.5em 2em;}
}

/* IMAGES */
#page-content .scp-image-block {
    border-color: #1a1a1a;
    box-shadow: none;
}
#page-content .scp-image-block .scp-image-caption {
    border-color: #1a1a1a;
    background-color: black;
}

hr {
    background-color: var(--fg);
}
[[/module]]

