[[include :scp-wiki-cn:theme:admo]]

[[module CSS]]

:root {

--header-title: 'SCP-173';

--main-content-width: 1000rem;
--main-content-top-margin: 0rem;
--main-content-side-margin: 0vw;
--rate-module-background-color: var(--basalt-primary-color);
--search-icon-color: var(--basalt-main-text-color);
--scrollbar-thumb-color: var(--basalt-main-text-color), 0.6;
--selection-text-color: var(--basalt-light-text-color);

}

#main-content {
padding-bottom: 3rem;
padding-top: 0;
}

#header {
background: rgb(var(--admo-color-dark));
box-shadow: none;
}

#page-content {
margin-bottom: 3rem;
padding-block: unset;
}

#action-area {
margin: auto;
padding-top: 3rem;
max-width: 70rem;
}

#page-options-container, #main-content > .page-tags {
margin-inline: 3vw;
}

#page-content > p:nth-of-type(1), #page-content > p:nth-of-type(2) {
margin: 0;
}

/* =============================================== */

.admohub-rate {
position: absolute;
top: calc(var(--header-final-height) + 10rem);
left: 0;
right: 0;
text-align: center;
z-index: 2;
}

.admohub-splash {
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
height: calc(100vh - var(--header-final-height));
background-image: linear-gradient(0deg, rgb(0, 0, 0) 0%, rgba(0,0,0,0.8) 100%), url(https://scp-wiki.wikidot.com/local--files/admonition/ball.gif);
background-size: cover;
background-position: center;
background-repeat: no-repeat;
}

.admohub-title {
display: flex;
justify-content: center;
align-items: center;
width: 100vw;
height: 100%;
backdrop-filter: blur(8px);
background-image: radial-gradient(circle, rgb(148, 148, 148) 0%, rgb(30, 30, 30) 70%);
background-repeat: no-repeat;
background-position: center;
background-size: 300%;
-webkit-background-clip: text !important;
-webkit-text-fill-color: transparent;
font-size: 16vw;
font-family: var(--header-font);
animation: title 25s ease infinite;
}

.admohub-title p {
letter-spacing: -1.2vw;
}

@keyframes title {

0% {
background-position: left;
}

50% {
background-position: right;
}

100% {
background-position: left;
}

}

.admohub-quote {
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
width: 45vw;
height: 100vh;
margin: auto auto 10vh;
font-family: 'Roboto Condensed', var(--UI-font);
font-weight: bold;
font-size: 2.5vw;
text-align: center;
color: rgb(133, 133, 133);
line-height: 2.5vw;
}

.admohub-quote > span {
font-family: 'Sofia Sans', var(--UI-font);
}

.admohub-quote::after {
content: "博·伯翰";
margin-top: 2vw;
font-size: 50%;
font-weight: 900;
opacity: 60%;
}

.admohub-container {
display: flex;
flex-flow: column nowrap;
justify-content: center;
}

.admohub-episode {
display: flex;
flex-flow: row nowrap;
justify-content: center;
align-items: stretch;
height: 100vh;
box-sizing: border-box;
}

.admohub-epimage {
background-image: linear-gradient(-90deg, rgb(0, 0, 0) 0%, rgba(0, 0, 0, 0) 100%), linear-gradient(45deg, rgb(0, 0, 0) 0%, rgba(0, 0, 0, 0) 30%), var(--admohub-epbg);
background-size: cover;
background-position: 65% center;
width: 50vw;
}

#u-6488 .admohub-epimage {
image-rendering: pixelated;
}

.admohub-epdesc {
display: flex;
flex-flow: column nowrap;
justify-content: space-evenly;
position: relative;
background-image: linear-gradient(-245deg, rgb(0, 0, 0) 6%, rgba(var(--admohub-epcolor), 0.13) 100%);
width: 50vw;
padding: 2.5vw 3vw;
box-sizing: border-box;
}

.admohub-epdirector {
position: absolute;
top: 11vh;
right: 5vw;
font-family: 'Roboto Condensed', var(--UI-font);
font-size: 1.3vw;
font-weight: bold;
}

.admohub-epdirector::before {
content: "执笔 ";
margin-right: 0.9vw;
font-weight: lighter;
opacity: 80%;
}

.admohub-eptitle {
font-family: var(--header-font);
font-size: 5vw;
line-height: 3.8vw;
letter-spacing: -0.15vw;
color: rgb(158, 158, 158);
}

.admohub-epno {
display: block;
font-family: 'Roboto Condensed', var(--UI-font);
font-size: 2vw;
font-weight: bold;
opacity: 70%;
}

a.admohub-eplink, a.admohub-accesslink {
display: block;
max-width: 19vw;
padding: 1.3vw 1vw;
background-color: rgba(0, 0, 0, 0.3);
border: solid 0.2vw rgba(255, 255, 255, 0.15);
font-family: 'Roboto Condensed',var(--UI-font);
font-weight: bold;
text-decoration: none !important;
color: rgb(var(--basalt-main-text-color));
text-align: center;
transition-duration: 0.1s;
}

a.admohub-eplink {
font-size: 2.5vw;
line-height: 3vw;
}

a.admohub-accesslink {
font-size: 1vw;
line-height: 1vw;
opacity: 60%;
}

a.admohub-eplink:hover, a.admohub-accesslink:hover {
color: rgb(32, 32, 32);
border-color: transparent;
background-color: rgba(var(--basalt-main-text-color), 0.9);
}

a.admohub-accesslink:hover {
opacity: 100%;
}

.admohub-title, .admohub-epno, .admohub-eptitle {
user-select: none;
}

.admohub-about {
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
height: 135vh;
max-width: 30rem !important;
margin: auto;
font-family: var(--UI-font);
word-spacing: 4px;
}

/* =============================================== */

@media only screen and (max-width: 1230px) {

.admohub-epimage {
width: 45vw;
}

.admohub-epdesc {
width: 55vw;
padding: 0.5vw 0vw;
}

.admohub-epdirector {
font-size: 2vw;
}

.admohub-epno {
font-size: 3vw;
}

.admohub-eptitle {
font-size: 6vw;
line-height: 4.8vw;
}

a.admohub-eplink, a.admohub-accesslink {
max-width: 28vw;
padding: 2.3vw 1vw;
}

a.admohub-eplink {
font-size: 3.5vw;
line-height: 4vw;
}

a.admohub-accesslink {
font-size: 1.6vw;
line-height: 1.6vw;
opacity: 70%;
}

}

@media only screen and (max-width: 940px) {

.admohub-splash {
background-image: none;
}

.admohub-quote {
font-size: 3.5vw;
line-height: 3.5vw;
}

.admohub-epimage {
display: none;
}

.admohub-epdesc {
width: 100%;
padding: 5.5vw 2.5vw;
background-image: linear-gradient(90deg, rgb(0, 0, 0) 30%, rgba(0,0,0,0.4) 100%), linear-gradient(220deg, rgb(0, 0, 0) 0%, rgba(0,0,0,0) 40%), var(--admohub-epbg);
background-size: cover;
background-position: left center;
}

.admohub-epdirector {
top: 5vw;
right: 8vw;
font-size: 2.6vw;
}

.admohub-epno {
font-size: 4vw;
}

.admohub-eptitle {
font-size: 8vw;
line-height: 6.3vw;
}

a.admohub-eplink, a.admohub-accesslink {
max-width: 36vw;
padding: 3.3vw 2vw;
}

a.admohub-eplink {
font-size: 4.5vw;
line-height: 5vw;
}

a.admohub-accesslink {
font-size: 2.3vw;
line-height: 2.3vw;
}

}

@media only screen and (max-width: 760px) {

.admohub-splash {
height: 65vh;
}

.admohub-quote {
width: 80%;
font-size: 4.5vw;
line-height: 3.5vw;
}

.admohub-epdesc {
align-items: center;
background-image: linear-gradient(180deg, rgba(0, 0, 0, 0.9) 0%, rgba(0,0,0,0.3) 100%), var(--admohub-epbg);
background-position: center;
text-align: center;
}

.admohub-epdirector {
font-size: 3.3vw;
opacity: 70%;
}

.admohub-epdirector::before {
display: none;
}

.admohub-epno {
font-size: 5vw;
}

.admohub-eptitle {
font-size: 10vw;
line-height: 8.3vw;
}

a.admohub-eplink, a.admohub-accesslink {
max-width: unset;
background-color: rgba(0, 0, 0, 0.6);
padding: 4.3vw 9vw;
}

a.admohub-eplink {
font-size: 7.5vw;
line-height: 8vw;
}

a.admohub-accesslink {
font-size: 3.5vw;
line-height: 4vw;
}

.admohub-about {
height: 120vh;
max-width: unset !important;
margin-inline: 3vw;
font-size: 3vw;
text-align: justify;
}

}

