[[module CSS]]
.page-rate-widget-box { float: right; }
.page-rate-widget-box .rate-points { display: inline-block; font-size: 0; min-height: 13px; }
.page-rate-widget-box .rate-points::before, .page-rate-widget-box .rate-points .number
{ font-size: 13px; }
.contentbox1 .page-rate-widget-box .rate-points::before { content: "ÌèâÍ∞Ä(rating): "; }
