.contentbox3 .page-rate-widget-box .rate-points::before { content: "rating: "; }
.page-rate-widget-box .rate-points::before { font-size: 13px; content: "rating: "; }
#comments-options-hidden a { font-size: 0; }
#comments-options-hidden a::before { content: "See Comment"; font-size: 13px; }
#page-info { font-size: 0; }
#page-info::before, #page-info span { font-size: 11px; }
#page-info::before { content: "page revision: null, last edited:" }
.page-watch-options { display: none; }

.licensebox .collapsible-block-link {
	margin-left: 0.25em;
	padding: 0.25em;
	font-weight: bold;
	opacity: 0.5;
	color: inherit;
	transition: opacity 0.5s ease-in-out;
}

.licensebox .collapsible-block-link:hover,
.licensebox .collapsible-block-link:active {
	opacity: 1;
}

a#edit-button { font-size: 0; }
a#edit-button::before { font-size: 12px; content: "Edit"; }
a#pagerate-button { font-size: 0; }
a#pagerate-button span { font-size: 12px; }
a#pagerate-button::before { font-size: 12px; content: "Rate ("; }
a#pagerate-button::after { font-size: 12px; content: ")"; }
a#tags-button { font-size: 0; }
a#tags-button::before { font-size: 12px; content: "Tags"; }
a#discuss-button { font-size: 0; }
a#discuss-button::before { font-size: 12px; content: "Discuss (Now)"; }
a#history-button { font-size: 0; }
a#history-button::before { font-size: 12px; content: "History"; }
a#files-button { font-size: 0; }
a#files-button::before { font-size: 12px; content: "Files"; }
a#print-button { font-size: 0; }
a#print-button::before { font-size: 12px; content: "Print"; }
a#site-tools-button { font-size: 0; }
a#site-tools-button::before { font-size: 12px; content: "Site tools"; }
a#more-options-button { font-size: 0; }
a#more-options-button::before { font-size: 12px; content: "+ Options"; }

#footer { font-size: 0; }
#footer::before, #footer a, #footer .options { font-size: 10px }
#footer::before { content: "Powered by "; }
#footer .options a { font-size: 0; }
#footer .options a::before { font-size: 10px }
#footer .options a:nth-child(1)::before { content: "Help"; }
#footer .options a:nth-child(2)::before { content: "Terms of Service"; }
#footer .options a:nth-child(3)::before { content: "Privacy"; }
#footer .options a:nth-child(4)::before { content: "Report a bug"; }
#footer .options a:nth-child(5)::before { content: "Flag as objectionable"; }

#license-area { margin: 8px 0; }
#license-area, #license-area a, #license-area strong { font-size: 0; }
#license-area strong::before {
 font-size: 14px;
 content: "üö´: Every contents of the SCP Foundation are not allowed for the children under 15.";
}
#license-area a::before {
 font-size: 14px;
 content: "Unless otherwise stated, the content of this page is licensed under ";
 color: black;
}
#license-area a::after {
 font-size: 14px;
 content: "Creative Commons Attribution-ShareAlike 3.0 License";
}
