  border-left: 1px solid #36752D;
}

.greenT table tbody td {
  border-left: 1px solid #C6FFC2;
}

.greenT table tbody td:first-child {
color: #275420;
}
/*
ボックス
*/
.warning {
    position: relative;
    margin: 2em 0;
    padding: 25px 10px 7px;
    border: solid 2px #FFC107;
}
.warning h1{
    position: absolute;
    margin-top: 0;
    display: inline-block;
    top: -2px;
    left: -2px;
    padding: 0 9px;
    height: 25px;
    line-height: 25px;
    vertical-align: middle;
    font-size: 17px;
    background: #FFC107;
    color: #ffffff;
    font-weight: bold;
}
.warning p {
    margin: 0;
    padding: 0;
}
.danger {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  border-radius: 20%;
  transform: rotate(45deg);
  flex-flow: column wrap;
  vertical-align: top;
  width: 1em;
  height: 1em;
  border: 2px solid red;
}
.danger p{
  font-weight:bold;
  transform: rotate(-45deg);
}

/*
サイド
*/

#side-bar {
  min-height: calc(100% + 7em);
  background-color:var(--thin-blue);
  transform: translateY(-6em);
}

#side-bar .side-block {
  border: none;
  border-radius: 0;
  box-shadow: none;
  background: transparent !important;
  margin-top: 4em;
}
#side-bar .heading {
    color:var(--mainblue);
    border-bottom: solid 1px var(--mainblue);
}
#side-bar .side-block a {
  color: #fff;
}

/*
めであ
*/

@media (max-width: 767px) and (min-width:440px) {

  #container {
    border-left: 9px solid var(--thin-blue);
  }
  #header {
    background-size: 200px, 180px;
  }
  div #header h1 {
    width: 200px;
    min-height: 100px;
  }
  #side-bar {
  transform: translateY(0em);
}
}

@media (max-width: 440px) {
  #container {
    border-left: 3px solid var(--thin-blue);
  }
  #header {
    background-size: 130px, 140px;
    height: 100px;
  }
  div #header h1 {
    width: 200px;
    min-height: 80px;
  }
  #header::after {
    margin-top: 100px;
    height: 100px;
    background: url(http://scp-jp.wdfiles.com/local--files/joicle-catalog-hub/q3-min-min.png) top left repeat-x;
    background-size: 440px;
  }
  #top-bar {
    margin-top: 33px;
  }
  #main-content {
    margin-top: 30px;
  }
}
@media (max-width: 767px) {
    #side-bar:target .close-menu {
        min-width: 100vw;
        min-height: 100vh;
    }
}
img.close-menu {
    opacity: 0 !important;
}
