.authorlink-wrapper > a {
  width:inherit !important;
  text-align:center !important;
  margin:auto;
  color: #000;
}


.authorlink-wrapper > a::before {color: #f00 !important;}

.scp-image-block.block-fullwidth {
  width: 100vw !important;
  max-width: none !important;
  position: relative;
  left: calc(-50vw + 50%);
  margin-top:-5.5rem;
}

/* ACS tweaks */

.anom-bar {
margin-top:1.2rem !important;
}

.text-part .contain-class > div, .text-part .second-class > div, .text-part .disrupt-class > div, .text-part .risk-class > div {
    width: 100%;
    font-size: 0.75em;
    padding-top: 2px;
}

.top-right-box > .level {
  font-size: 1.6em !important;
  line-height: 0.2 !important;
}

hr + .anom-bar-container, .anom-bar-container:not(first-child) {
  padding-top: 0em !important;}

.contain-class .class-text::before {color: white !important;}

.danger-diamond > .bottom-icon {background-color: white;}

/* Embiggening text for accessibility */

#main-content {font-size:1.1rem;}

/* Susa, OK header */

.susaHeader {
  width: 100vw;
  max-width: none !important;
  position: relative;
  left: calc(-50vw + 50%);
  color:#f73636;
  text-align: center;
  font-size: 3.5rem;
  font-weight: bold;
  border-top: #f73636 8px solid;
  border-bottom: #f73636 8px solid;
  text-transform: uppercase;
  letter-spacing: 10pt;
  margin: 3rem 0rem;
}

.susaHeader p {
  line-height: 0.7rem;
  font-family: 'Sans Normalcy','Nanum Gothic',Arial,sans-serif;
}

