.addendumBox {
    margin: 25px 4% 25px 2%;
    padding: 4% 6% 4% 6%;
    background-color: #f9f9f9;
    color: #3e3434;
    box-shadow: 10px 10px 3px rgba(0,0,0,0.25);
    border-left: 5px solid #f73636;
}

.addendumBox tt {
  color: #595553;
}

.timeStampSCP {
  letter-spacing:0.2rem;
  font-family:sans-serif;
  font-weight:900;
  font-size: 80%;
  margin: -0.5rem;
  color: #b93030;
  line-height:1.5rem;
  text-decoration:underline;
}

.characterTag {
  letter-spacing:0.2rem;
  font-family:sans-serif;
  font-weight:bold;
  text-decoration:underline;
  line-height:2.3rem;
  text-transform:uppercase;
  font-size: 80%;
  margin: -0.5rem;
