@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@400;700&display=swap');

#page-content {
  --in-disconnected-show-delay: 1s;
  --in-disconnected-icon: url("	http://scpkosb.wikidot.com/local--files/draft:vocadomato-11-38d1/internet-dis256.png");
  --in-disconnected-background: url("http://scpkosb.wikidot.com/local--files/draft:vocadomato-502-e863/scp_trans_black.png");
  --in-disconnected-background-color: #e2e8f0;
  --in-disconnected-hide-delay: 8.2s;
  --in-disconnected-hide-animation: internet-disconnected-hide;
}

@keyframes internet-disconnected-show-position{
  0% {opacity:0; transform:translate(0, 20%)}
  100% {opacity:1; transform:translate(0, 0%)}
}

@keyframes internet-disconnected-show-scale{
  0% {transform: scale(75%)}
  100% {transform: scale(100%)}
}

@keyframes internet-disconnected-hide { 0% { opacity: 1; } 100% { opacity: 0; transform: translateY(30%);  visibility: hidden;}}

#page-content .internet-disconnected p::selection { background:rgba(0, 0, 0, 0); color: #000000; }
#page-content .internet-disconnected p::-moz-selection { background:rgba(0, 0, 0, 0); color: #000000; }
#page-content .internet-disconnected span::selection { background:rgba(0, 0, 0, 0); color: #000000; }
#page-content .internet-disconnected span::-moz-selection { background:rgba(0, 0, 0, 0); color: #000000; }

#page-content .internet-disconnected {
  z-index: 10;
  position: fixed;
  bottom: 20px;
  right: 20px;
  width: 350px;

  font-size: 150%;
  text-align: left;
  vertical-align: middle;

  /*transform: translate(-50%, -50%);*/

  animation: var(--in-disconnected-hide-animation) 0.5s;
  animation-delay: var(--in-disconnected-hide-delay);
  animation-fill-mode: both;
  animation-timing-function: ease-in;
}

#page-content .internet-disconnected .offline-icon {
    background: var(--in-disconnected-icon) center no-repeat;
    background-size: 80px;
    width: 80px;
    height: 80px;
    position: absolute;
    top: 50%;
    left: 5px;
    transform: translate(0, -50%);
}

#page-content .internet-disconnected {
    font-family: 'Noto Sans KR', sans-serif;
    line-height: 120%;
}

#page-content .internet-disconnected p {
  margin: 4px;
  line-height: 120%;
}

#page-content .internet-disconnected .title {
  color: #000000;
  position: static;
  width: 100%;
  font-size: 125%;
  font-weight: 700;
  display: inline;
}

#page-content .internet-disconnected .content {
  color: #000000;
  font-family: 'Noto Sans KR', sans-serif;
  font-weight: 400;
  position: static;
  width: 100%;
  display: inline;
}

#page-content .internet-disconnected .internet-disconnected-inside {
  animation: internet-disconnected-show-position 0.42s;
  animation-delay: var(--in-disconnected-show-delay);
  animation-fill-mode: both;
}

#page-content .internet-disconnected .internet-disconnected-inside2 {
  vertical-align: middle;
  border-radius: 10px;
  box-shadow: 0px 7px 16px #343e5a79;
  background: var(--in-disconnected-background) center no-repeat;
  background-size: 300px;
  background-color: var(--in-disconnected-background-color);
  padding: 0.75em 1.2em 0.75em 80px;

  animation: internet-disconnected-show-scale 0.42s;
  animation-delay: var(--in-disconnected-show-delay);
  animation-timing-function: cubic-bezier(.41,.74,.45,1.43);
  animation-fill-mode: both;
}

@media (max-width: 400px) {
#page-content .internet-disconnected {
  width: 90vw;
  right: 5vw;
}

#page-content .internet-disconnected .offline-icon {
    background-size: 20vw;
    width: 20vw;
    height: 20vw;
}

#page-content .internet-disconnected .title {
  font-size: 6.5vw;
}

#page-content .internet-disconnected .content {
  font-size: 5vw;
}

#page-content .internet-disconnected .internet-disconnected-inside2 {
  box-shadow: 0px 7px 16px #343e5a79;
  background: var(--in-disconnected-background) center no-repeat;
  background-size: 80vw;
  background-color: var(--in-disconnected-background-color);
  padding: 0.75em 5vw 0.75em 20vw;
}
}
