@import url("http://1119s.wdfiles.com/local--code/credit-mod-for-basalt-update/1");

:root {
    --basalt-bright-element-color: 11, 0, 128;
    --basalt-dark-element-color: 11, 0, 128;
}
.classification-header .right-box {
    --level0: 11,0,128;
    font-weight: bold;
}
@media (min-width: 768px) {
    #main-content {
        max-width: 80%;
    }
    .header-font-size {
        font-size:1.25em;
    }
}
#page-content {
    font-size: 100%;
    min-height: 720px;
}
div.rate-box-with-credit-button {
    margin-bottom: 1em;
}
:is(#u-credit-view,#u-credit-otherwise) {
    --basalt-UI-opacity: 0.5;
}
:is(#u-credit-view,#u-credit-otherwise) .modalcontainer {
    display: flex;
    max-width: 100%;
    height: 100%;
}
:is(#u-credit-view,#u-credit-otherwise) .modalcontainer .modalbox {
    width: 400px;
    height: 300px;
}
[[/module]]
