// Replace old docCookies with localStorage proxy (with fallback)
var docCookies = new ( function() {
    var data = {};
    var useLocalStorage = typeof window.localStorage !== 'undefined';

    this.getItem = function( key ) {
        if ( useLocalStorage ) {
            return localStorage.getItem( key );
        }

if ( typeof data[ key ] === 'undefined' ) {
            return null;
        }

        return data[ key ];
    };

    this.setItem = function( key, value ) {
        if ( useLocalStorage ) {
            localStorage.setItem( key, value );
        } else {
            data[ key ] = value;
        }
    };

    this.removeItem = function( key, value ) {
        if ( useLocalStorage ) {
            localStorage.removeItem( key );
        } else {
            delete data[ key ];
        }
    };

    this.hasItem = function( key ) {
        var result = this.getItem( key );

        return result !== null || typeof result === 'undefined';
    };
} );

String.prototype.hashCode = function(){
    var hash = 0;
    if (this.length == 0) return hash;
    for (i = 0; i < this.length; i++) {
     char = this.charCodeAt(i);
     hash = ((hash<<5)-hash)+char;
     hash = hash & hash; // Convert to 32bit integer
    }
    return hash;
}


