[[module css]]
.acs-text {
    display: grid;
    grid-template-areas:
        "item item clear"
        "contain disrupt disrupt"
        "contain risk risk";
    grid-template-rows: 1fr 1fr 1fr;
    grid-template-columns: 25% 1fr 1fr;
    grid-gap: 0.125rem;
    width: 100%;
    height: 6.5rem;
    background-color: rgb(var(--black-monochrome, 12, 12, 12));
    line-height: 1;
    position: relative;
}

.acs-text > a {
    display: block;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    color: rgb(0,0,0,0);
}

@media (max-width: 500px ) {
    .acs-text {
        grid-template-areas:
            "item item clear"
            "contain contain contain"
            "disrupt disrupt disrupt"
            "risk risk risk";
        grid-template-rows: 2fr 2fr 1fr 1fr;
        height: 10rem;
        max-height: 50vh;
    }
}

.acs-text > div {
    background-color: rgb(var(--white-monochrome, 252, 252, 252));
    padding: 0.25rem;
    display: flex;
    align-items: center;
}

.acs-text > div strong {
    margin-right: 0.25rem;
}

.acs-text > .acs-text-item {
    grid-area: item;
    font-size: 1.5em;
}

.acs-text > .acs-text-clear {
    grid-area: clear;
    font-size: 1.25em;
}

.acs-text > .acs-text-contain {
    grid-area: contain;
    font-size: 1.25em;
}

.acs-text > .acs-text-disrupt {
    grid-area: disrupt;
}

.acs-text > .acs-text-risk {
    grid-area: risk;
}
[[/module]]

[[module css]]
