    --four-color: 255,109,0;
    --three-color: 255,211,0;
    --two-color: 0,135,189;
    --one-color: 0,159,107;
}

.acs-hybrid {
    display: -ms-grid;
    display: grid;
        grid-template-areas:
        "item item clear"
        "contain disrupt disrupt"
        "contain risk risk";
    -ms-grid-rows: 1fr 0.125rem 1fr 0.125rem 1fr;
