[[module CSS]]

span.skew {
  display:inline-block;
  animation: skew 0.5s infinite;
  height: 1.41em;
}
@keyframes skew {
  0% {
    transform: none;
  }
  33% {
    transform: none;
  }
  33.3% {
    transform: skewX(45deg);
  }
  33.4% {
    transform: skewX(-45deg);
  }
  33.5% {
    transform: none;
  }
  66% {
    transform: none;
  }
  66.3% {
    transform: skewX(20deg);
  }
  66.4% {
    transform: skewX(-20deg);
  }
  66.5% {
    transform: none;
  }
  77% {
    transform: none;
  }
  77.3% {
    transform: skewX(30deg);
  }
  77.4% {
    transform: skewX(-30deg);
  }
  77.5% {
    transform: none;
  }
}

span.change-text205 > span {
    animation: change205 10s steps(1,end) infinite;
