[[module css]]
.fa-info:before {
    content: "\f091";
}
.contest {
background-image: linear-gradient(349deg, #A5A5A5, #BABAC2, #E8E8E8, #A5A5A5, #BABAC2);border: 1px solid transparent;
border-radius: 1px;
text-shadow: 1px 2px 3px rgba(0,0,0,0.35);
border-radius: 2px;
border-width: 2px;
color: #fff;
