@import url('https://fonts.googleapis.com/css2?family=Shippori+Mincho:wght@400;500;600;700&display=swap');

a {
    color: #f01010;
}
#page-content a {
    text-decoration: underline;
}
body {
    background: #202020;
    color: red;
    font-family: 'Shippori Mincho',serif;
}
div#container-wrap {
    background: none;
}
#page-title, .cooltext,h1 {
    color: #cd0000;
    background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6057712-13-8kst/ti.jpg);
    background-size: cover;
    background-position: center;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: saturate(2)brightness(1.3);
}
body {
    background: #202020;
    color: red;
    font-family: 'Shippori Mincho',serif;
    z-index: 0;
}
body:before {
    content: '';
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: -1;
    pointer-events: none;
    background-image: url(http://scp-jp-storage.wikidot.com/local--files/file:6057712-14-fgqn/ketsueki.jpg);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    filter: brightness(0.04)blur(5px)grayscale(1);
}
#header h1 a {
    font-family: 'Shippori Mincho',serif;
    padding: 0;
    display: block;
    line-height: 1;
    max-height: none;
    font-size: 50px;
    text-shadow: 0 0 10px #5605059c;
}
#header h1 {
    filter: saturate(1.5);
    display: flex;
    align-items: flex-end;
    height: 90px;
    margin-left: 5px;
}
#header h2 {
    margin-left: 5px;
    font-size: 15px;
}
#header h2 span {
    font-family: 'Shippori Mincho',serif;
    background: linear-gradient(to right,rgb(255 0 0 / 10%),rgb(255 0 0 / 50%)),url(http://scp-jp-storage.wikidot.com/local--files/file:6057712-10-my4p/%E5%A3%81.png);
    background-size: cover;
    background-position: center;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #828282;
    padding: 14px 0;
    filter: brightness(2);
    text-shadow: none;
}
div#header {
    background-image: none;
}
div#page-title {
    border-bottom: 1px solid #901;
    text-align: center;
    letter-spacing: 2px;
    padding: 5px 0;
    line-height: 1;
    font-weight: bold;
}
.scp-image-block {
    border: none;
    padding: 2px;
}
.scp-image-block .scp-image-caption {
    border: 1px solid red;
    background: #000;
    border-top: none;
    box-sizing: border-box;
}
body:after {
    content: "";
    display: block;
    position: fixed;
    top: -3px;
    left: -3px;
    bottom: -3px;
    right: -3px;
    z-index: 1000;
    pointer-events: none;
    animation: lines 0.1s infinite;
    background-size: 100% 3px, 2px 100%;
}
@keyframes lines {
0% {
    background-image: repeating-linear-gradient(
0deg
, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0) 2px, rgb(45 45 45 / 30%) 3px);
}
50% {
    background-image: repeating-linear-gradient(
0deg
, rgb(37 37 37 / 30%) 0px, rgba(0, 0, 0, 0) 1px, rgba(0, 0, 0, 0) 3px);
}
100% {
    background-image: repeating-linear-gradient(
0deg
, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0) 2px, rgb(49 49 49 / 30%) 3px);
}
}
input.button.btn {
    display: none;
}
input#search-top-box-input,
input#search-top-box-input:hover {
    background-color: rgb(255 255 255 / 5%) !important;
    outline: none !important;
    border: 2px solid #cc2b2b !important;
    box-shadow: 0 0 5px #cc2b2b;
    border-radius: 0 0 5px 5px;
    padding: 0;
    width: 22px;
    height: 22px;
    background-image: url("data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22%23cc2b2b%22%20d%3D%22M505%20442.7L405.3%20343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3%2044-79.7%2044-128C416%2093.1%20322.9%200%20208%200S0%2093.1%200%20208s93.1%20208%20208%20208c48.3%200%2092.7-16.4%20128-44v16.3c0%206.4%202.5%2012.5%207%2017l99.7%2099.7c9.4%209.4%2024.6%209.4%2033.9%200l28.3-28.3c9.4-9.4%209.4-24.6.1-34zM208%20336c-70.7%200-128-57.2-128-128%200-70.7%2057.2-128%20128-128%2070.7%200%20128%2057.2%20128%20128%200%2070.7-57.2%20128-128%20128z%22%2F%3E%3C%2Fsvg%3E");
    background-size: 70%;
    background-position: center;
    background-repeat: no-repeat;
    color: transparent;
    transition: .5s cubic-bezier(0.68, -0.55, 0.27, 1.55), .3s ease-in-out box-shadow;
}
input#search-top-box-input:focus {
    background-image: none;
    width: 10em;
    color: #cc2b2b;
    box-shadow: 0 0 7px #cc2b2b;
    border-radius: 5px 0 5px 5px;
}
div#search-top-box {
    top: 29px;
    right: 4px;
}
a#account-topbutton {
    width: 22px;
    height: 22px;
    display: inline-flex;
    padding: 0;
    border: 2px solid #cc2b2b !important;
    box-shadow: 0 0 5px #cc2b2b;
    border-radius: 5px 5px 0 0;
    color: #cc2b2b;
    font-size: 110%;
    align-items: center;
    justify-content: center;
    transition: .3s ease-in-out;
    transform: translateY(3px);
}
div#login-status {
    top: 2px;
    right: 5px;
    font-weight: bold;
    color: #ca4a4a;
}
a#account-topbutton:hover {
    box-shadow: 0 0 9px #cc2b2b, 0 0 5px #cc0a0a;
}
input#search-top-box-input:hover {
    box-shadow: 0 0 9px #cc2b2b, 0 0 5px #cc0a0a;
}
a#my-account {
    animation: blink 2s linear infinite .3s;
    color: rgb(255 255 255 / 80%);
}
#login-status>a>strong {
    font-weight: normal;
    color: #fff;
}
input#search-top-box-input.empty {
    color: transparent;
}
div#account-options {
    background: #fff;
    border: 2px solid #cc2b2b;
    width: 18em;
}
div#account-options ul {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    font-family: 'noto sans jp';
    font-weight: bold;
}
div#account-options li {
    flex-grow: 1;
    padding: 1px 2px;
    text-align: center;
    width: 50%;
    flex-basis: 50%;
    box-sizing: border-box;
    transition: .3s ease-in-out;
    color: #cc2b2b;
}
div#account-options li a {
    color: inherit !important;
    text-decoration: none;
    text-shadow: inherit;
}
div#account-options li:hover {
    background: #ffc0c0;
    text-shadow: none;
    color: #cc2b2b;
}
.mobile-top-bar {
    left: 5px;
}
#top-bar ul li a {
    border: none;
}
#top-bar ul li a {
    border: none;
}
#top-bar ul>li>ul>li>a, #top-bar ul>li:hover>ul>li>a {
    border: none;
    color: #cd0000;
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6057712-13-8kst/ti.jpg);
    background-size: cover;
    background-position: center;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: saturate(2)brightness(1.3);
    padding: 5px;
    flex-grow: 1;
}
#top-bar ul>li>ul>li {
    flex-grow: 1;
    display: flex;
}
#top-bar ul>li>ul {
    border: none;
    box-sizing: border-box;
    box-shadow: 2px 2px 4px #ff030336;
    background: #000;
    width: 13em;
    display: flex;
    flex-direction: column;
}
#top-bar ul>li>ul>li>a:hover,
#top-bar ul>li:hover>ul>li>a:hover {
    -webkit-text-fill-color: #000;
    background: linear-gradient(45deg,#a01c1c,red);
    filter: unset;
}
#top-bar ul li:hover a,#top-bar ul li.sfhover:hover a {
    background: transparent;
    border: none;
    color: #cd0000;
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6057712-13-8kst/ti.jpg);
    background-size: cover;
    background-position: center;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: saturate(2)brightness(1.3);
}
#html-body .creditRate .rateBox {
    display: inline-flex;
    border-bottom: 2px solid #db0e15;
}
#html-body .creditRate .rateBox div.page-rate-widget-box {
    border-bottom: none;
    box-shadow: none;
}
#html-body div.page-rate-widget-box .rate-points {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: #db0e15;
}
#html-body .page-rate-widget-box .rateup,
#html-body .page-rate-widget-box .ratedown {
    background-color: transparent;
        border: none;
}
#html-body .page-rate-widget-box .rateup a:hover,
#html-body .page-rate-widget-box .ratedown a:hover {
    background: transparent;
    color: rgb(219 14 21 / 0.70)
}
#html-body .page-rate-widget-box .cancel {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: #db0e15
}
#html-body .page-rate-widget-box .cancel a:hover {
    color: rgb(219 14 21 / 0.70);
    background: transparent
}
#html-body .modalbox .page-rate-widget-box {
    position: absolute;
    left: 50%;
    -webkit- transform: translateX(-50%);
    transform: translateX(-50%)
}
#html-body .rate-box-with-credit-button .creditButton {
    background-color: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none
}
#html-body a.fa.fa-info {
    color: #db0e15
}
#html-body a.fa.fa-info:hover {
    color: rgb(219 14 21 / 0.70)
}
#html-body .modalbox .modalbox-title {
    background: #000;
    color: #db0e15 ;
    border-radius: 0;
    text-shadow: 0 0 5px #db0e15;
    border-bottom: 1px solid #db0e15;
}
#html-body .modalbox {
    box-shadow: 0 2px 6px #db0e15;
    border-radius: 0;
    background-color: #000;
    border: 1px solid #db0e15;
}
#html-body .rate-box-with-credit-button a {
    text-decoration: none
}
#html-body .page-rate-widget-box .cancel a {
    color: #db0e15
}
#html-body .page-rate-widget-box .rateup a,
#html-body .page-rate-widget-box .ratedown a {
    color: #db0e15
}
#html-body div#u-credit-view, div#u-credit-otherwise {
    z-index: 20;
}
#html-body .page-rate-widget-box {
    border-bottom: 2px solid #db0e15;
    border-radius: 0;
}
#html-body .page-rate-widget-box, #html-body .creditRate .rateBox {
    background: rgb(0 0 0 / 30%);
    box-shadow: 0 0 3px #db0e15;
}
.modalbox-title h2:before {
    content: 'CREDIT';
    font-family: 'Shippori Mincho',serif;
}
.modalbox-title h2 span {
    display: none;
}
#side-bar .side-block {
    background: #000 !IMPORTANT;
    border-radius: 0;
    border: 3px solid #520707;
}
#side-bar .side-block a {
    text-decoration: none;
    color: red;
}
#side-bar .heading, #side-bar .collapsible-block-unfolded-link .collapsible-block-link,#side-bar .collapsible-block-unfolded-link {
    color: red;
    border-color: red;
}
#side-bar .menu-item > img {
     display: none;
}
#side-bar .collapsible-block-folded {
    background: none;
}
#side-bar .collapsible-block-folded .collapsible-block-link:before,
#side-bar .menu-item > img + a:before {
    content: "\f02e";
    display: inline-block;
    font-family: 'FontAwesome';
    font-size: .9em;
    font-weight: 400;
    padding: 0 .25em 0 0;
    text-align: center;
    width: 1em;
}
#side-bar .collapsible-block-folded .collapsible-block-link:before {
    content: "\f067";
}
#side-bar .menu-item > img[src*="home.png"] + a:before {
    content: "\f015";
}
#side-bar .menu-item > img[src*="default.png"] + a:before {
    content: "\f041";
}
#side-bar .menu-item > img[src*="series.png"] + a:before {
    content: "\f1c0";
}
#side-bar .menu-item > img[src*="help.png"] + a:before {
    content: "\f129";
}
#side-bar .menu-item > img[src*="forum.png"] + a:before {
    content: "\f086";
}
#side-bar .collapsible-block-link {
    margin-left: 0;
}
.open-menu a {
    text-decoration: none;
    font-weight: normal;
    border-radius: 0;
    background: #0009 !important;
    display: flex;
    justify-content: center;
    align-items: center;
    border: 3px solid rgb(255 0 0 / 70%) !important;
    color: rgb(255 0 0 / 70%) !important;
    bottom: 0;
    left: 0;
    transition: .3s ease-in-out;
}
.open-menu a:hover {
    box-shadow: 0 0 5px red;
}
@media (max-width: 767px) {
#side-bar:target,#side-bar {
    background: #000;
    border: none;
}
}
.cooltext {
    filter: saturate(2.5)brightness(1.5);
}
