#page-content .c-hr hr {
    margin: unset;
    padding: unset;
    height: unset;
    border: unset;
    color: unset;
    background-color: unset;
}
#page-content .c-hr hr {
    border: 0;
    height: 1px;
    background: linear-gradient(to left, rgba(255, 255, 255, 1) 0%, #000000 50%, rgba(255, 255, 255, 1) 100%);
    width: 85%;
    margin: 3em auto;
    position: relative;
    overflow: visible;
}

#page-content .c-hr hr::after {
    display: block;
    content: "";
    font-family: "FontAwesome";
    font-size: 15px;
    color: #000000;
    position: absolute;
    top: -20px;
    left: 50%;
    background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3995762-530-aoqa/eyeball.png) no-repeat;
    background-size: contain;
    background-color: var(--bg-color);
    width: 40px;
    height: 40px;
    transform: translateX(-50%);
}
#page-content .c-hr.shr hr {
    width: 30%;
    margin: 2em auto;
}
#page-content .c-hr.sshr hr {
    width: 15%;
    margin: 1em auto;
}
