[[module css]]
/*
ツイートボタン - 犬殴り
Author: dog_punch
url: http://dog-punch.wikidot.com/sintax:tweet
CC BY-SA 3.0

リンクの取得が構文の関係で上手くいかなかったので直接引っ張ってきました。申し訳ない......
*/

@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;500&display=swap');

a.shareontwitter {
    color: #fff;
    display: inline-flex;
    align-items: center;
    text-decoration: none !IMPORTANT;
    background: #00acee;
    padding: 5px 7px;
    border: 2px solid white;
    box-shadow: 0 0 5px #949494;
    font-weight: 600;
    font-family: "Segoe UI", Meiryo, system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
    transition: .3s ease-in-out;
}
a.shareontwitter:hover {
    opacity: .65;
}
a.shareontwitter:before {
    font-family: fontawesome;
    content: '\f099';
    margin-right: 5px;
    font-weight: normal;
    font-size: 1.3em;
}

a.shareontwitter.tweetred {
    background: #b01
}

a.shareontwitter.tweetbrown {
    background: #633
}

a.shareontwitter.tweetgray {
    background: #292929
}

a.shareontwitter.tweetgreen {
    background: #69856f
}

a.shareontwitter.tweetblue {
    background: #313b75
}

a.shareontwitter.tweetserif {
    font-family: 'Playfair Display', serif;
}

.modalbox a.shareontwitter {
    position: absolute;
    bottom: 10px;
    left: 10px;
    font-size: 0px;
    box-shadow: none;
    border-color: #ccc;
    z-index: 1;
}
.modalbox a.shareontwitter:before {
    font-size: 16px;
    margin: 0;
}
.modalbox a.shareontwitter:hover {
    box-shadow: none;
}
.tweetbutton .list-pages-box {
    display: flex;
}
.tweetbutton.tweetcenter .list-pages-box {
    justify-content: center;
}
.tweetbutton.tweetleft .list-pages-box {
    justify-content: flex-start;
}
.tweetbutton.tweetright .list-pages-box {
    justify-content: flex-end;
}
.tweetbutton p {
    margin: 0;
}
a.shareontwitter.tweettrue:before {
    font-size: 16px;
    margin: 0;
}
a.shareontwitter.tweettrue {
    font-size: 0px;
    border-radius: 50%;
    height: 30px;
    width: 30px;
    padding: 0;
    display: flex;
    justify-content: center;
    align-items: center;
}

