<script type="text/javascript">
/** Attention : si vous modifiez ce texte, prenez en compte toute modification du nombre de caractères. Si vous ajoutez ou supprimez des caractères, mettez à jour la liste POSITIONS. **/
const CONTENU = '<p>SCP-299-FR为一由五（5）个字符所组成之信息，其会随机感染基金会使用字符记录之文件、代码及网络：不论是电子或书面文件，只要包含字符，即会被其所替代。</p>\n\n<p>SCP-299-FR由MTF Omega-11 “沙3盒秃鹰” 于此前在各类次要文件中发现大量重复之相同字符串后被初次发现。</p>\n\n<p>起初，SCP-299-FR被认为仅是一次单纯的异常事件。后来，随着时1称13-Fr事件的多次出现，许多收容专家建议将其重归类为Safe。</p>\n\n<p>多年来，SCP-299-FR一直被归类为Safe，直到11-3 Bravo事件的发生：通过间接使得安保核导弹被突然启动，其导致Site-██被彻底摧毁。</p>\n\n<p>后来事实表明，SCP-299-FR对代码进行了修改，替换了激活代码中的所有字符；随后通过基金会内部网络发送一条包含SCP-299-FR（时已成为核弹启动密码）的信息，引爆了核弹。</p>'

function remplace(texte, position) {
    return texte.slice(0, position) + "113-B" + texte.slice(position + 5);
}

const PARAGRAPHE = document.getElementById("desc");

const POSITIONS = [[16, 34, 46, 58, 75], [107, 125, 134, 144], [178, 204, 218], [255, 280, 301], [330, 343, 361, 384, 396]];

function remplaceParagraphe(p_no) {
    for(let i = 0; i < POSITIONS[p_no].length; i++) {
        PARAGRAPHE.innerHTML = remplace(PARAGRAPHE.innerHTML, POSITIONS[p_no][i]);
    }
}

const BOUTON = document.createElement("button");
BOUTON.innerText = "解密描述";

const DELAI = 10000;

BOUTON.addEventListener("click", () => {
    PARAGRAPHE.innerHTML = CONTENU;
    for(let i = 0; i < POSITIONS.length; i++) {
         setTimeout( () => remplaceParagraphe(i), (i + 1) * 10000);
    }
});

PARAGRAPHE.appendChild(BOUTON);

</script>

