[[module css]]
#page-content .pseudo-vote {
  display: block;
  margin: 1em auto;
  width: 90%;
  max-width: 400px;
  padding: 1em;
  border: solid 1px rgba(0, 0, 0, 0.25);
  border-radius: 1em;
}

#page-content .pseudo-vote .pseudo-vote-question {
  font-size: 115%;
  line-height: 1.5;
  margin: 0.2em 0;
}

/* TABのデフォルトスタイルをキャンセル */

#page-content .pseudo-vote .yui-content {
  display: none;
}

#page-content .pseudo-vote .yui-navset .yui-nav,
#page-content .pseudo-vote .yui-navset .yui-nav a,
#page-content .pseudo-vote .yui-navset .yui-nav a em {
  border: none;
}

#page-content .pseudo-vote .yui-navset .yui-nav .selected a em {
  color: black;
  padding: 0.25em 0.75em;
}

#page-content .pseudo-vote .yui-navset .yui-nav a,
#page-content .pseudo-vote .yui-navset .yui-nav a:focus,
#page-content .pseudo-vote .yui-navset .yui-nav a:hover {
  background: none;
}

#page-content .pseudo-vote .yui-navset .yui-nav li:nth-of-type(1) {
  display: none;
}

/* 調整 */
#page-content .pseudo-vote .yui-navset .yui-nav li {
  display: block;
  position: relative;
  font-size: 14px;
  line-height: 2;
  margin: 5px 1em;
  pointer-events: none;
}

#page-content .pseudo-vote .yui-navset .yui-nav li:nth-of-type(1).selected ~ li {
  pointer-events: auto;
}

/* デフォルト背景 */
#page-content .pseudo-vote .yui-navset .yui-nav li::before,
#page-content .pseudo-vote .yui-navset .yui-nav li:nth-of-type(1).selected ~ li::before {
  content: "";
  position: absolute;
  top: 0px;
  left: 0px;
  background: rgba(51, 49, 49, 0.25);
  width: 10px;
  height: 100%;
  border-radius: 5px;
  transition: ease-in-out 0.25s;
  pointer-events: none;
}

#page-content .pseudo-vote .yui-navset .yui-nav li:hover::before,
#page-content .pseudo-vote .yui-navset .yui-nav li:nth-of-type(1).selected ~ li:hover::before {
  width: 50px;
}

/* 選択 */
/* 選んだほう */
#page-content .pseudo-vote .yui-navset .yui-nav li.selected::before {
  width: 75%;
  background: rgba(0, 161, 233, 0.45);
}

#page-content .pseudo-vote .yui-navset .yui-nav li.selected::after {
  content: "\f058";
  font-family: "FontAwesome";
  position: absolute;
  top: 0;
  right: 1em;
  display: inline-block;
  height: 100%;
  line-height: 36px;
  font-size: 20px;
  color: rgba(0, 0, 0, 0.5);
}

/* 選んでない方 */
#page-content .pseudo-vote .yui-navset .yui-nav li:not(.selected)::before {
  width: 25%;
  background: rgba(0, 161, 233, 0.3);
}
