[[module CSS]]
#main-content hr {
     border-top: .0625rem solid rgb(160, 44, 44) !important;
}

h2, h3, h4, h5, h6 {
     margin: 0.5em 0 0.4em !important;
}

.text-part .disrupt-class::before, .text-part .risk-class::before {
     color: rgb(252, 252, 252) !important;
}

:is(div.image-block, div.scp-image-block) {
    gap: 0 !important;
     border: solid 5px 5px 5px 0 #212121;
     box-shadow: none;
     border-bottom: solid 3px red !important;
}


:is(div.image-block, div.scp-image-block) :is(.image-caption, .scp-image-caption) {
     border-top: none;
     box-shadow: none;
     background: #212121 !important;
     color: #fff;
     padding: 5px !important;
     font-size: 90% !important;
}


.sub-title {
     border-bottom: solid 0.1em #000; font-size: 150%;
     border-image: linear-gradient(90deg, rgba(0,0,0,1) 0%, rgba(0,0,0,1) 70%, rgba(255,255,255,0) 95%, rgba(255,255,255,0) 100%);
     border-image-slice: 1;
}

.sub-title p {
     line-height: 0%;
     margin-bottom: 0.5em;
}

.n-box {
     position: relative;
     margin: 1em 0em 0em;
     padding: 0.5em 0.5em 0 0.5em;
     border-left: solid 4px rgb(160, 44, 44);
     background: transparent;
     line-height: 141%;
}
.n-box .n-box-title {
     position: absolute;
     top: 0px;
     left: 0px;
     background: rgb(160, 44, 44);
     color: #fff;
     padding: 5px;
     font-weight: bold;
}

@media (max-width: 769px) {
    .imaging {
     display: none;
    }
}

@media (min-width: 768px) {
    .imaging2 {
     display: none;
    }
}

.box {
    border-top: solid 3px #545454;
    background-color: #f4f4f4;
    padding: 0.2em 0.5em;
}


@media only screen and (min-width:600px) {
  .box {
    margin: 1em 0;
  }
}

@media only screen and (max-width:599px) {
  .box {
    margin-bottom: 1em;
  }
}

.side-box {
    border: solid 0.125rem #fff;
    box-shadow: -0.188rem 0rem 0rem -0.006rem rgb(160, 44, 44);
    background-color: #f4f4f4;
    padding: 0.2em 0.5em;
}

@media only screen and (min-width:600px) {
  .side-box {
    margin: 1em 0;
  }
}

@media only screen and (max-width:599px) {
  .side-box {
    margin-bottom: 1em;
  }
}

.cover-word {
     background: rgb(160, 44, 44);
     border-radius: 4px;
     padding: 3px;
     color: white;
}

.earthworm a {
     font-size: 90% !important;
}
