[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400&display=swap');

html {
    overflow-y: scroll;
}

div#container-wrap,
div#header {
     background-image: none;
}


#container-wrap {
    --c-header: linear-gradient(180deg, black 25%, #673d3d 100%);

    --c-topbar: linear-gradient(180deg, #535353 0%, #3c3c3c 53%);
}

div#container-wrap {
    background: url("http://scp-jp-storage.wdfiles.com/local--files/file%3A3396310-48-gmqo/header-bg.png") top left repeat-x, var(--c-header) top left repeat-x;
    background-size: 100px 141px;
}

div#container-wrap::after {
    content: "";
    display: block;
    position: absolute;
    top: 140px;
    left: 0;
    width: 100%;
    height: 23px;
    background: var(--c-topbar) top left repeat-x;
}

 :root{
   --header-title: "SCP 재단";
   --header-subtitle: "확보, 격리, 보호";
   --titleColor: #ffffff;
   --subtitleColor: #ffffff;
}


@media (min-width: 768px) {
   #main-content {
        max-width: 70%;
    }
}

#search-top-box {
    top: 2rem;
    right: 0;
}


@media (max-width: 768px) {
   #header h1 a::before {
       position: relative;
       bottom: 0.5em;
   }
   #header h2::before {
       position: relative;
       bottom: 0.5em;
       top: auto;
   }
   #header::before {
       position: absolute;
       background-position: center top 0.5em;
       background-size: auto 9.5em;
   }
}


#search-top-box-input {
   display: none;
}


#login-status {
    left: auto;
    right: 0;
}


.creditRate {
    margin-right: 0em;
}


body {
 text-align: justify;
}

#content-wrap {
    min-height: auto;
}
[[/module]]


