[[module css]]
/* ---- HEADER ---- */
div#container-wrap {
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A5644609-152-xhsj/Background.png) top left repeat-x;
}

#header h1, div#header::before {
    filter: blur(2px);
}

#header {
    background-image: none;
}

div#header::before {
    content: "";
    position: absolute;
    top: 0;
    left: -3px;
    width: 150px;
    height: 135px;
    background-image: linear-gradient(-45deg, transparent, #FFF), url("https://scp-jp-storage.wdfiles.com/local--files/file%3A4052664-4-vbf6/scp-logo.svg");
    background-size: 6.7rem, 6.7rem;
    background-position: 3px 29px, 3px 29px;
    background-repeat: no-repeat, no-repeat;
}


#login-status {
    filter: drop-shadow(0 0 5px #000);
}

#login-status,
#login-status a {
    color: #000;
    font-weight: bold;
}

#account-topbutton{
    border: 0px;
}

#search-top-box {
    bottom: 0;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #000;
    box-shadow: 0 0 5px #000;
    border-radius: 3px;
    color: #FFF;
}

#search-top-box input.empty {
    color: #DDD;
}

div#header h1 a {
    font-weight: bold;
    color: #000;
    text-shadow: none;
}

div#header h1 a::before {
    content: "SCP財団";
}

div#header h1 a span {
    display: none;
}

div#header h2 span {
    font-weight: bold;
    color: #000;
    padding: 17px 0;
    text-shadow: none;
    white-space: pre;
    filter: blur(1px);
}


#header h1 a {
    color: black;
    text-shadow: none;
}

#header h2 span {
    color: black;
    text-shadow: none;
    filter: blur(1px);
}

/* ---- PAGE RATING ---- */

div.page-rate-widget-box {
    box-shadow: none !important;
    border-bottom: 1px solid #181b39;
    border-radius: 0;
}
.creditRate .rateBox {
    display: inline-flex;
    border-bottom: 1px solid #181b39;
}
.creditRate .rateBox div.page-rate-widget-box {
    border-bottom: none !important;
}
div.page-rate-widget-box .rate-points {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: #181b39;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent;
        border: none;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: transparent !important;
    color: #29292942 !important
}

.page-rate-widget-box .cancel {
    background-color: transparent;
    border: none;
    border-radius: 0;
    color: #181b39 !important;
}

.page-rate-widget-box .cancel a:hover {
    color: #29292942 !important;
    background: transparent !important;
}

.modalbox .page-rate-widget-box {
    position: absolute;
    left: 50%;
    transform: translateX(-50%)
}

.rate-box-with-credit-button .creditButton {
    background-color: transparent !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
}

a.fa.fa-info {
    color: #181b39 !important
}

a.fa.fa-info:hover {
    color: #29292942 !important
}

.modalbox .modalbox-title {
    background: #004150 !important;
    color: #fff !important;
    border-radius: 0 !important;
}

.modalbox {
    box-shadow: 0 2px 6px #ccc !important;
    border-radius: 0 !important;
    background-color: #fff !important;
}

.rate-box-with-credit-button a {
    text-decoration: none !important
}

.page-rate-widget-box .cancel a {
    color: #181b39 !important
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #181b39 !important
}

[[/module]]

[[module CSS]]
.blockquote.file {
  background: #fff;
  border: 1px solid #999;
  padding: 0;
  box-shadow: 0 1px 6px rgba(0,0,0,.25);
}

.file-heading {
  position: relative;
  color: #fff;
  font-weight: bold;
  padding: .5em 1em;
}

.file-heading::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    120deg,
    transparent 0%,
    transparent 6em,
    #fff 6em,
    #fff 6.5em,
    transparent 6.5em,
    transparent 7em,
    #fff 7em,
    #fff 7.5em,

    transparent 7.5em,
    transparent 8em,
    #fff 8em,
    #fff 100%
  );
}

.file-heading.video {
  background: hsl(0deg, 60%, 40%);
}

.file-heading.video::after {
  content: "映像記録";
}

.file-heading.audio {
  background: hsl(240deg, 60%, 40%);
}

.file-heading.audio::after {
  content: "通信記録";
}

.file-heading.document {
  background: hsl(0deg, 0%, 30%);
}

.file-heading.document::after {
  content: "文書記録";
}

.file-content {
  padding: 0 1em;
}

.file-content .blockquote {
  background: transparent;
}

.email {float: center; border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px 10px 10px 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)} a.collapsible-block-link { font-weight: bold; }
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon}

.color-black{
color:#000000;
}
[[/module]]

