<style>
.loading {
  color: #880000;
  position: fixed;
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
 text-align:center;
}

.loading.hide {
  opacity: 0;
  pointer-events: none;
  transition: opacity 500ms;
}

.loading .circle {
  display: block;
  top: calc( 50% - 20px );
  width: 40px;
  height: 40px;
  margin: 0 auto;
  border: 7px solid #994444;
  border-top: 7px solid #FFCCCC;
  border-radius: 50px;
  animation: loading 700ms linear 0ms infinite normal both;
}

@keyframes loading {
  0% { transform: rotate( 0deg ); }
  100% { transform: rotate( 360deg ); }
}
</style>
<div class="loading">
<span class="circle">
