.anchor{position:relative;height:0;z-index:5}div.skip_side{font-size:.875em;position:absolute;top:0;left:calc(100% + 0.75rem);width:min(calc((100vw - var(--main-content-width))/2 - 2rem),75%);padding:.325rem .875rem;margin:0;overflow-y:auto;box-sizing:border-box;scrollbar-width:none}div.skip_side::-webkit-scrollbar{width:0rem}@media (max-width:1290px){.skip_side{position: relative !important;top: unset !important;left: unset !important;width: unset !important;}.anchor {height: unset;}}

div.skip_side {
    font-size: .875em;
