/*構文引用: dog_punch氏 サイバーパンクなの  |  http://dog-punch.wikidot.com/sintax:photo-guriguri*/

.wrapper img {
  display: block;
  border: none;
}


.wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0;
  padding: 0;
}

.wrapper {
  width: 100%;
  margin: auto;
  overflow: hidden;
}

.viewport {
  position: relative;
  overflow: hidden;
}
.viewport::before {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 2;
  pointer-events: none;
  animation: lines 0.1s infinite;
  background-size: 100% 3px, 2px 100%;
}
.main-img {
  position: relative;
  animation: twitch 0.1s steps(3, end) infinite;
}

.line {
  pointer-events: none;
  overflow: hidden;
  position: absolute;
  top: 0;
  left: 0;
  height: 5px;
  width: 100%;
  visibility: hidden;
  animation: slideBottom 12s ease-out infinite;
}
.line::after {
  content: "";
  left: 0;
  top: 0;
  position: absolute;
  height: 100%;
  width: 100%;
  opacity: .1;
}
.line-img {
  animation: twitch 0.1s steps(3, end) infinite;
}
.line-supwrap {
  position: absolute;
  top: 0;
  left: 0;
  transform: translateX(-2px);
  animation: slideTop 12s ease-out infinite;
}
@keyframes slideBottom {
  0% {
    top: 0px;
    visibility: visible;
  }
  100% {
    top: 746px;
  }
}
@keyframes slideTop {
  0% {
    top: 0px;
  }
  100% {
    top: -746px;
  }
}
@keyframes twitch {
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-0.3px);
  }
  100% {
    transform: translateY(0px);
  }
}
@keyframes lines {
  0% {
    background-image: repeating-linear-gradient(0deg, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0) 2px, rgba(0, 0, 0, 0.3) 3px);
  }
  50% {
    background-image: repeating-linear-gradient(0deg, rgba(0, 0, 0, 0.3) 0px, rgba(0, 0, 0, 0) 1px, rgba(0, 0, 0, 0) 3px);
  }
  100% {
    background-image: repeating-linear-gradient(0deg, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0) 2px, rgba(0, 0, 0, 0.3) 3px);
  }
}

