    </div>
    <div id="uniControls">
        <div id="uniControlsHeader">多元宇宙合作计划网络<br/>控制台</div>
        <div id="uniList">
            <div style="text-align:center; margin-top: 3em;"><i>正在连接……</i></div>
            <div class="storyMode">
                数据库连接错误："错误1146 (42S02)：表格 'mcpdb.宇宙_列表' 不存在"<br/>
                <div style="text-align: center; color: black; margin-top:2em;">正在尝试解决问题……</div>
                <div id="loadbar">
                    <div id="loadbarprogress" style="width:0%">0%</div>
                </div>
                问题解决前，请使用“随机同行宇宙”功能<br/><br/>另外，若您拥有合适权限，请使用监督者紧急覆写功能。
            </div>
            <div class="freeMode" style="display:none;">
                <div style="text-align:center;">连接成功。</div>
                <div style="text-align:left;font-size:1.3em">多元宇宙列表</div>
                <ul id="mainUniList">
                </ul>
            </div>
        </div>
        <div id="randomUni" class="scpbtn">随机同行宇宙</div>
        <div class="storyMode" style="border: 1px solid black; padding: 1.5em; margin-top: 3em;">
            <p id="o5controls_button" class="showButton" style="text-align:center;" onclick="toggleShow(this.id, 'o5controls', '[打开O5控制台]', '[关闭O5控制台]')">[打开O5控制台]</p>
            <div id="o5controls" style="display: none; width: 100%;">
                <p style="text-align: center; font-size: 1.3em; margin: 0; font-weight: bold;">监督者紧急覆写</p>
                <p><b>警告：</b>若非拥有O5权限或特殊MCP网络证书，请勿使用该功能。</p>
                <div style="text-align:center;">
                    <p>请输入覆写密码：</p>
                    <input type="password" id="o5pass" style="box-sizing: border-box;">
                    <div id="o5button" class="scpbtn" style="width: 12em; margin: 1em auto;">尝试覆写</div>
                    <p id="o5denied" style="display:none;color:red; font-weight:bold; font-size:1.3em;">请求拒绝</p>
                </div>
            </div>
        </div>
    </div>
</body>
<script>
    $( document ).ready(function() {
        var storyDone = false;
        var storyMax = 7;
        var storyEnd = 8;

        var universeData = function(newId, newTitle, newDiv, newWarning = "", newExclude = false) {
            this.id = newId;
            this.title = newTitle;
            this.div = newDiv;
            this.warning = newWarning;
            this.exclude = newExclude;
        };

        var universeList = [];
        var currentUniverse = 0;
        var o5access = false;

        function updateLoadbar(newPercent) {
            var loadbar = document.getElementById("loadbarprogress");
            loadbar.style.width = newPercent.toFixed(2) + '%';
            loadbar.innerHTML = newPercent.toFixed(2) * 1 + '%';
        }


        function selectUniverse(uniData) {
            $('#header').hide();
            $('#main').hide();
            $('#uniControls').hide();
            $('#connecting').show();
            if(o5access) {
                 $('#o5approved').show();
            }

            $('.current').removeClass('current');
            $('#' + uniData.div).addClass('current');
            $('#uniTitle').text(uniData.title);

            if(uniData.warning !== "") {
                $('#uniWarning').text(" " + uniData.warning);
                $('#uwarning').show();
            } else {
                $('#uwarning').hide();
            }

            setTimeout(function(){
                $('#header').show();
                $('#main').show();
                $('#uniControls').show();
                $('#o5approved').hide();
                $('#connecting').hide();
                o5access = false;
            }, 3000);
        }

        function randomUniverse(universeArray) {
            if(storyDone) {
                var randomUni = currentUniverse;
                while(randomUni === currentUniverse || universeArray[randomUni].exclude) {
                    randomUni = Math.floor(Math.random() * (universeArray.length-1)) + 1;
                }
                selectUniverse(universeArray[randomUni]);
                currentUniverse = randomUni;
            } else {
                currentUniverse++;
                curStoryUniverse = currentUniverse;

                var loadPercent = currentUniverse/storyMax * 100.0;
                if(loadPercent >= 100.0) {
                    loadPercent = 99.0;
                } else if(loadPercent <= 0.0) {
                    loadPercent = 1.0;
                }
                updateLoadbar(loadPercent);

                if(curStoryUniverse > storyMax) {
                    curStoryUniverse = 0;
                }
                selectUniverse(universeArray[curStoryUniverse]);
                if(currentUniverse === storyEnd) {
                    storyDone = true;
                    $('.storyMode').hide()
                    $('#uniList .freeMode').show();
                }
            }
        }

        function findUniverseLocation(id) {
             for(var i = 0; i < universeList.length; i++) {
                 if(universeList[i].id.toString() === id) {
                     return i;
                 }
             }
             return -1;
        }

        function compareUni(a,b) {
            if (a.id < b.id)
                return -1;
            if (a.id > b.id)
                return 1;
            return 0;
        }

        function populateUniList() {
            var newUniList = universeList.slice();
            newUniList.sort(compareUni);

            var oLi = $("<li>").append("UNI-零（起源） - [连接中断]");
            $("#mainUniList").append(oLi);

            for(var i = 0; i < newUniList.length; i++) {
                uni = newUniList[i];
                if(uni.exclude === true){
                    continue;
                }

                var newLi = $("<li>").append("<span>" + uni.title + "</span>");
                newLi.attr('id', '#uni-' + uni.id);
                newLi.click(function () {
                       var uniLoc = findUniverseLocation($(this).attr('id').substring(5));
                       if(uniLoc !== -1) {
                           selectUniverse(universeList[uniLoc]);
                           currentUniverse = uniLoc;
                       }
                });
                $("#mainUniList").append(newLi);
            }
        }

        $('#randomUni').click(function() {
            randomUniverse(universeList);
        });

        $('#o5button').click(function() {
            if($('#o5pass').val() === "forgetmenot") {
                storyDone = true;
                $('.storyMode').hide()
                $('#uniList .freeMode').show();
                o5access = true;
                selectUniverse(universeList[currentUniverse]);
            } else if($('#o5pass').val() === "iremember") {
                storyDone = true;
                $('.storyMode').hide()
                $('#uniList .freeMode').show();
                o5access = true;
                currentUniverse = 7;
                selectUniverse(universeList[currentUniverse]);
            } else {
                $('#o5denied').show();
            }
        });

        // 故事模式
        universeList.push(new universeData(2346, "UNI-2346（本地）", "initial", "用户不可访问本地宇宙。请尝试连接另一同行宇宙。"));
        universeList.push(new universeData(2347, "UNI-2347（平行）", "parallel"));
        universeList.push(new universeData(86243, "UNI-86243（经典）", "classic"));
        universeList.push(new universeData(1100, "UNI-1100（中古）", "medieval"));
        universeList.push(new universeData(3236, "UNI-3236（销售）", "dado", "用户无法访问此宇宙。请尝试连接另一同行宇宙。"));
        universeList.push(new universeData(2049, "UNI-2049（未来）", "future"));
        universeList.push(new universeData(7411, "UNI-7411（未知-04）", "unknownspots", "源语言仍在破译中。系统将尝试翻译已知语句。"));
        universeList.push(new universeData(0000, "UNI-零（起源）", "zero", "错误：无法<信息>，尝试<解决>。", true));    

        // 自由模式
        // 第三步：把代码连接到你的div上。
        /* 使用以下的代码来添加新宇宙：

            universeList.push(new universeData(UNI_NUMBER, UNI_NAME, UNI_DIV_ID, WARNING_TEXT));


                UNI_NUMBER    ：宇宙的编码（必须是数字）。
                UNI_NAME        ：宇宙的名称，格式为“UNI-XXXX（名称）”。
                UNI_DIV_ID      ：你给div添加的“id”。
                WARNING_TEXT ：可选项，能在迭代最上面加入红色字体的“警告”。
        */

        // -- 此行以下添加新宇宙 --

        universeList.push(new universeData(4321, "UNI-4321（玛丽苏）", "msue", "源文件可能包含不可靠和/或冗余内容。"));








        // -- 此行以上添加新宇宙 --

        populateUniList();

        selectUniverse(universeList[0]);
    });
</script>
