        <!-- ^^^ ADD NEW UNIVERSE DIVS **ABOVE** THIS LINE ^^^ -->

    </div>
    <div id="uniControls">
        <div id="uniControlsHeader">Multiversal Collaboration Project Network<br/>Control Panel</div>
        <div id="uniList">
            <div style="text-align:center; margin-top: 3em;"><i>Connecting...</i></div>
            <div class="storyMode">
                Database Connection Error: "ERROR 1146 (42S02): Table 'mcpdb.universe_list' doesn't exist"<br/>
                <div style="text-align: center; color: black; margin-top:2em;">Attempting to resolve issue...</div>
                <div id="loadbar">
                    <div id="loadbarprogress" style="width:0%">0%</div>
                </div>
                Please try using the "Random Peer Universe" function until this issue has been fixed. <br/><br/>Alternatively, if you have sufficient clearance, use the Emergency Overseer Override.
            </div>
            <div class="freeMode" style="display:none;">
                <div style="text-align:center;">Connection Successful.</div>
                <div style="text-align:left;font-size:1.3em">Multiverse List</div>
                <ul id="mainUniList">
                </ul>
            </div>
        </div>
        <div id="randomUni" class="scpbtn">Random Peer Universe</div>
        <div class="storyMode" style="border: 1px solid black; padding: 1.5em; margin-top: 3em;">
            <p id="o5controls_button" class="showButton" style="text-align:center;" onclick="toggleShow(this.id, 'o5controls', '[Open O5 Control Panel]', '[Close O5 Control Panel]')">[Open O5 Control Panel]</p>
            <div id="o5controls" style="display: none; width: 100%;">
                <p style="text-align: center; font-size: 1.3em; margin: 0; font-weight: bold;">EMERGENCY OVERSEER OVERRIDE</p>
                <p><b>Warning:</b> Do not attempt to use unless you have either O5 clearance, or special MCP Network credentials.</p>
                <div style="text-align:center;">
                    <p>Please enter override passcode:</p>
                    <input type="password" id="o5pass" style="box-sizing: border-box;">
                    <div id="o5button" class="scpbtn" style="width: 12em; margin: 1em auto;">Attempt Override</div>
                    <p id="o5denied" style="display:none;color:red; font-weight:bold; font-size:1.3em;">ACCESS DENIED</p>
                </div>
            </div>
        </div>
    </div>
</body>
<script>
    $( document ).ready(function() {
        var storyDone = false;
        var storyMax = 7;
        var storyEnd = 8;

        var universeData = function(newId, newTitle, newDiv, newWarning = "", newExclude = false) {
            this.id = newId;
            this.title = newTitle;
            this.div = newDiv;
            this.warning = newWarning;
            this.exclude = newExclude;
        };

        var universeList = [];
        var currentUniverse = 0;
        var o5access = false;

        function updateLoadbar(newPercent) {
            var loadbar = document.getElementById("loadbarprogress"); 
            loadbar.style.width = newPercent.toFixed(2) + '%'; 
            loadbar.innerHTML = newPercent.toFixed(2) * 1 + '%'; 
        }


        function selectUniverse(uniData) {
            $('#header').hide();
            $('#main').hide();
            $('#uniControls').hide();
            $('#connecting').show();
            if(o5access) {
                 $('#o5approved').show();
            }

            $('.current').removeClass('current');
            $('#' + uniData.div).addClass('current');
            $('#uniTitle').text(uniData.title);

            if(uniData.warning !== "") {
                $('#uniWarning').text(" " + uniData.warning);
                $('#uwarning').show();
            } else {
                $('#uwarning').hide();
            }

            setTimeout(function(){ 
                $('#header').show();
                $('#main').show();
                $('#uniControls').show();
                $('#o5approved').hide();
                $('#connecting').hide();
                o5access = false;
            }, 3000);
        }

        function randomUniverse(universeArray) {
            if(storyDone) {
                var randomUni = currentUniverse;
                while(randomUni === currentUniverse || universeArray[randomUni].exclude) {
                    randomUni = Math.floor(Math.random() * (universeArray.length-1)) + 1;
                }
                selectUniverse(universeArray[randomUni]);
                currentUniverse = randomUni;
            } else {
                currentUniverse++;
                curStoryUniverse = currentUniverse;

                var loadPercent = currentUniverse/storyMax * 100.0;
                if(loadPercent >= 100.0) {
                    loadPercent = 99.0;
                } else if(loadPercent <= 0.0) {
                    loadPercent = 1.0;
                }
                updateLoadbar(loadPercent);

                if(curStoryUniverse > storyMax) {
                    curStoryUniverse = 0;
                }
                selectUniverse(universeArray[curStoryUniverse]);
                if(currentUniverse === storyEnd) {
                    storyDone = true;
                    $('.storyMode').hide()
                    $('#uniList .freeMode').show();
                }
            }
        }

        function findUniverseLocation(id) {
             for(var i = 0; i < universeList.length; i++) {
                 if(universeList[i].id.toString() === id) {
                     return i;
                 }
             }
             return -1;
        }

        function compareUni(a,b) {
            if (a.id < b.id)
                return -1;
            if (a.id > b.id)
                return 1;
            return 0;
        }

        function populateUniList() {
            var newUniList = universeList.slice();
            newUniList.sort(compareUni);

            var oLi = $("<li>").append("UNI-ZERO (ORIGIN) - [CONNECTION LOST]");
            $("#mainUniList").append(oLi);

            for(var i = 0; i < newUniList.length; i++) {
                uni = newUniList[i];
                if(uni.exclude === true){
                    continue;
                }

                var newLi = $("<li>").append("<span>" + uni.title + "</span>");
                newLi.attr('id', '#uni-' + uni.id);
                newLi.click(function () {
                       var uniLoc = findUniverseLocation($(this).attr('id').substring(5));
                       if(uniLoc !== -1) {
                           selectUniverse(universeList[uniLoc]);
                           currentUniverse = uniLoc;
                       }
                });
                $("#mainUniList").append(newLi);
            }
        }

        $('#randomUni').click(function() {
            randomUniverse(universeList);
        });

        $('#o5button').click(function() {
            if($('#o5pass').val() === "forgetmenot") {
                storyDone = true;
                $('.storyMode').hide()
                $('#uniList .freeMode').show();
                o5access = true;
                selectUniverse(universeList[currentUniverse]);
            } else if($('#o5pass').val() === "iremember") {
                storyDone = true;
                $('.storyMode').hide()
                $('#uniList .freeMode').show();
                o5access = true;
                currentUniverse = 7;
                selectUniverse(universeList[currentUniverse]);
            } else {
                $('#o5denied').show();
            }
        });

        // Story Mode
        universeList.push(new universeData(2346, "UNI-2346 (LOCAL)", "initial", "Local will be inaccessible to users. Please try connecting to another peer universe."));
        universeList.push(new universeData(2347, "UNI-2347 (PARALLEL)", "parallel"));
        universeList.push(new universeData(86243, "UNI-86243 (CLASSIC)", "classic"));
        universeList.push(new universeData(1100, "UNI-1100 (MEDIEVAL)", "medieval"));
        universeList.push(new universeData(3236, "UNI-3236 (SALES)", "dado", "This universe is inaccessible to users. Please try connecting to another peer universe.")); 
        universeList.push(new universeData(2049, "UNI-2049 (FUTURE)", "future"));
        universeList.push(new universeData(7411, "UNI-7411 (UNKNOWN-04)", "unknownspots", "Source language still subject to translation process. The system will attempt to translate known phrases."));
        universeList.push(new universeData(89000, "UNI-ZERO (ORIGIN) - [CONNECTION REESTABLISHED ‐ UPDATE]", "zero", "ERROR : FAILED TO <MESSAGE>, TRY <SOLUTION>."));    

        // Free Mode 
        // STEP THREE: Connecting the code to your div.
        /* Use this example code to add a new universe:

            universeList.push(new universeData(UNI_NUMBER, UNI_NAME, UNI_DIV_ID, WARNING_TEXT));


                UNI_NUMBER    : The number of the universe (must be a number).
                UNI_NAME        : The name of the universe in the format "UNI-XXXX (NAME)".
                UNI_DIV_ID      : The "id" you gave your div.
                WARNING_TEXT : Optional field. Adds that red 'warning' text at the top of some iterations.
        */

        // -- ADD NEW UNIVERSES BELOW THIS LINE -- 

        universeList.push(new universeData(4321, "UNI-4321 (M. SUE)", "msue", "Source may contain unreliable and/or redundant information."));








        // -- ADD NEW UNIVERSES ABOVE THIS LINE -- 

        populateUniList();

        selectUniverse(universeList[0]);
    });
</script>
