[[module CSS]]
@import url('https://scp-jp-storage.wdfiles.com/local--files/file%3A6903570-62-hmq8/ReincarnationThemeCSS.css');
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400..900&display=swap');

:root {
    --fnColor: #008cc8;
}

.doc {
    position: relative;
    margin: 2em 0;
    padding: 1.5em 1em 0.5em;
    border: solid 1px #121212;
    background: #FFFFFF;
}
.doc .doc-title {
    position: absolute;
    display: inline-block;
    top: -13px;
    left: 10px;
    padding: 5px 9px;
    border: solid 1px #121212;
    background: #FFFFFF;
    line-height: 1;
    font-size: 1.2em;
    font-weight: bold;
}

.email {
    float: center;
    border: solid 2px #000000;
    width: 88%;
    padding: 1px 15px;
    margin: 1em auto;
}
.tofrom {
    margin-left: 10px;
    margin-top: 5px;
    padding: 1px 15px;
    border-left: solid 3px maroon;
}

#u-ratingboxhid {   
    opacity:0.3;
    transition: all 0.5s ease-out;
}
#u-ratingboxhid:hover {  
    opacity:1;
}

.classified-bar-wrapper {
    position: relative;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: repeat(2, 1fr);
    margin-bottom: 1em;
}
.classified-bar-text {
    position: absolute;
    font-family: "Orbitron", serif;
    font-weight: bold;
    font-size: 16px;
    white-space: nowrap;
    color: #FFFFFF;
    mix-blend-mode: difference;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.level {
    position: relative;
    grid-area: 1 / 1 / 2 / 2;
    height: 60px;
    margin: 1px;
    background: linear-gradient(135deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-right: solid 3px #000000;
    clip-path: polygon(60px 0, 100% 0, 100% 100%, 0 100%);
}
.classified {
    position: relative;
    grid-area: 2 / 1 / 3 / 2;
    height: 60px;
    margin: 1px;
    background: linear-gradient(45deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-right: solid 3px #000000;
    clip-path: polygon(0 0, 100% 0, 100% 100%, 60px 100%);
}
.item-number {
    position: relative;
    grid-area: 1 / 2 / 2 / 3;
    height: 60px;
    margin: 1px;
    background: linear-gradient(-135deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-left: solid 3px #000000;
    clip-path: polygon(0 0, calc(100% - 60px) 0, 100% 100%, 0 100%);
}
.class {
    position: relative;
    grid-area: 2 / 2 / 3 / 3;
    height: 60px;
    margin: 1px;
    background: linear-gradient(-45deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-left: solid 3px #000000;
    clip-path: polygon(0 0, 100% 0, calc(100% - 60px) 100%, 0 100%);
}
.icon {
    position: absolute;
    width: 60px;
    height: 60px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #EDEDED url("http://scp-jp-storage.wdfiles.com/local--files/file%3A6759963-101-k1n1/Tiamat.svg") center no-repeat;
    background-size: cover;
    border: solid 3px transparent;
    border-radius: 50%;
}

@media (max-width: 767px) {
  .classified-bar-text {
    font-size: 12px;
  }
}
[[/module]]
