[[module CSS]]

/* HEADER */
#header{ background-image: url(http://scp-wiki.wdfiles.com/local--files/scp-4428/DoMC_Logo100.png); }
#header h1 a{
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0;
    max-height: 0;
    color: transparent;
    background: transparent;
    font-family: 'Verdana', Arial, sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}
#header h1 a::before{
    content: "误传部门";
    color: white;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px black;
}
#header h2 span{
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0;
    max-height: 0;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before{
    content: '筛除 传讯 偏误';
    color: white;
    text-shadow: 1px 1px 1px black;
}

.notice{
    background: #b54d3b;
    outline: 4px solid #b54d3b;
    border: 1px solid #fdf560;
    padding: 0.78em;
    color: #fdf560;
    text-align: center;
    font-weight: bold;
    word-break: unset;
}

.red{
    color: #FF0000;
}

.h2{
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
    font-size: 150%;
    font-family: "Trebuchet MS", Trebuchet, Verdana, Arial, Helvetica;
}

.quote-block{
    border: 3px double #999;
    background-color: #d8e5f0;
    padding: .25em 1em;
    margin-bottom: 1em;
}
[[/module]]

