[[html]]
<script type="text/javascript">

function LogIn(){
loggedin=false;
password="5438464";
password=prompt("PASSWORD:","");
password=password.toLowerCase();

if (password=="5438464") {
loggedin=true;
window.open('http://ja.scp-wiki.net/scp-444-ko');
}

if (password=="4423144444") {
loggedin=true;
window.open('http://ja.scp-wiki.net/scp-444-ko-document');
}

if (loggedin==false) {
alert("アクセス制限: パスワードが違います。この言葉を見てすぐに跪き、両腕を上げてセキュリティ担当員を待ちなさい。");
}
}
</script>
<div style="margin:0 auto; width: 150px;">
<form><input type=button value="◈追加情報にアクセス◈" onClick="LogIn()"></form>
