@import url('https://fonts.googleapis.com/css?family=Poppins&amp;display=swap');

.lostmedia-comment,
.lostmedia-comment * {
    box-sizing: border-box;
}

.lostmedia-comment * {
    font-family: 'Poppins', sans-serif;
}
.lostmedia-comment {
    margin-top:5px;
    margin-bottom:5px;
    overflow:hidden;
    background-color: #fff;
}
.lostmedia-comment .comment {
    position:relative;
}
.lostmedia-comment .reply-comment {
    margin-top:10px;
    margin-left:30px;
}
.lostmedia-comment .head-comment > .comment-header {
    border-top:1px solid #00a7d8;
}
.lostmedia-comment .target-comment .comment-header {
    border:2px solid green;
}
.lostmedia-comment .comment-header {
    background-color:#e4f1ff;
}
.lostmedia-comment .reply-comment > .comment-header {
    background-color:#f5faff;
}
.lostmedia-comment .comment-header {
    padding-top:2px;
    padding-bottom:2px;
    background-color:#f9f9f9 !important;
    width:100%
}
.lostmedia-comment .head-comment > .comment-header {
    border-top: 4px solid #36782c;
}
.lostmedia-comment .comment-header-left {
    display:inline-block;
    vertical-align:middle;
}
.lostmedia-comment .comment-header-center {
    display:inline-block;
    vertical-align:middle;
    padding-left:5px;
}
.lostmedia-comment .comment-header-right {
    display:inline-block;
    float:right;
}
.lostmedia-comment .avatar {
    height:48px;
    padding:5px;
}
.lostmedia-comment .comment-title {
    font-size:20px;
    font-weight:bold;
}
.lostmedia-comment .comment-author {
    padding-right:5px;
    font-size:12px;
}
.lostmedia-comment .comment-author a {
    color:#00a7d8;
    font-weight:bold;
}
.lostmedia-comment .collapsible-block {
    position: relative;
}
.lostmedia-comment .collapsible-block .collapsible-block-link {
    position: absolute;
    top: -50px;
    right: 0;
    width: 32px;
    height: 32px;
    box-sizing: content-box;
    padding: 0 5px;
    color: rgba(0,0,0,0);
}
.lostmedia-comment .collapsible-block .collapsible-block-link:hover {
    background-color:#8eddf5;
    mix-blend-mode: darken;
}
.lostmedia-comment .comment-body {
    margin-bottom:5px;
    font-size:14px;
    padding-left: 5px;
}
.lostmedia-comment .comment-details {
    color:#555555;
    opacity:0.8;
    font-size:12px;
    padding-right:5px;
    padding-left:5px;
}
.lostmedia-comment .button {
    background-color:transparent;
    border:none;
    font-family:sans-serif;
    font-weight:bold;
    text-decoration:none;
    padding-left:5px;
    padding-right:5px;
    box-shadow:none;
    -webkit-appearance: button;
    cursor: pointer;
    text-transform: none;
    overflow: visible;
    margin: 0;
}
.lostmedia-comment .button:hover {
    background-color:#8eddf5;
}
.lostmedia-comment .button:enabled:active {
    opacity:0.5;
}
.lostmedia-comment .button:enabled:hover {
    color:#3ccefa;
}
.lostmedia-comment .button:disabled {
    color:#95a5a6;
    opacity:0.2;
}
.lostmedia-comment .moderator-button {
    color:#ff0000;
}
.lostmedia-comment .moderator-button:enabled:hover {
    color:#ff5555;
}
.lostmedia-comment .toggle-button {
    border:none;
    border-style:none;
}
.lostmedia-comment .vote-upcount,
.lostmedia-comment .vote-downcount {
    color:#555555;
    padding-left:3px;
}
.lostmedia-comment .link-button {
    border:none;
    border-style:none;
    font-size:12px;
    font-weight:normal;
    text-align:left;
    position: relative;
}

.lostmedia-comment .link-button a,
.lostmedia-comment .link-button a:hover,
.lostmedia-comment .link-button a:active,
.lostmedia-comment .link-button a:visited {
    text-decoration: none;
    color: #333;
}
.lostmedia-comment .reply-button {
    font-size:12px;
    font-weight:normal;
    text-align:left;
}
.lostmedia-comment div.span {
    display: inline-block;
}

[[/module]]

