[[module CSS]]
@import url(https://cdn.jsdelivr.net/font-nanum/1.0/nanumbarungothic/nanumbarungothic.css);
@import url("https://fonts.googleapis.com/css?family=Poppins:600,800|Space+Mono:400,400i,700,700i&display=swap&subset=latin-ext");

#page-title {
    display: none;
}

.wide1, .wide2 {
  display: block;
}

.flex {
  display: flex;
}

.mini1, .mini2 {
  display: none;
}

.classimage {
  background-size: 104px;
  background-position: center right 5px;
  background-repeat: no-repeat;
}

.classtext {
  display: inline-block;
  font-weight: 600;
}

.A {
  margin-top: 4px;
  font-size: 40px;
}

.B {
  margin-top: -4px;
  font-size: 30px;
}

.C {
  margin-bottom: 6px;
  font-size: 25px;
}

.hover {
  text-decoration: none;
  border-bottom: none;
  color: #B01;
  line-height: 40px;
}
.hover:hover {
  cursor: pointer;
  text-decoration: none;
  border-bottom: none;
}

.hover span.hh {    display: none;}
.hover:hover span.hh {
  z-index: 10;
  position: absolute;
  display: inline;
  height: auto;
  width: auto;
  max-width: 600px;
  min-width: 400px;
  background: #FFF;
  border: 1px solid #000;
  color: black;
  font-family: verdana, "Nanum Gothic", verdana, arial, helvetica, sans-serif;
  font-size: 13px;
  font-weight: 400;
  line-height: 16px;
  padding: 0.5em;
}
.hover:hover span span {
  position: relative;
  margin: auto;
  height: auto;
  width: auto;
  border: none;
  padding: 0;
}

.left, .right {
  margin: 27px -30%;
}

#page-content .collapsible-block-link {
  color: #333;
  font-size: 1.35em;
  font-weight: 600;
}

hr {
  width: 100%;
  margin-left: 0;
}

@media ( max-width: 1200px) {
  .right {
    margin: 27px -50%;
  }
}

@media ( max-width: 960px ) {
  .hover:hover span.hh {
    width: 85vw;
    left: 50%;
    transform: translateX(-50%);
    min-width: 0;
  }

  .left, .right {
    margin: 27px 0;
  }

  .wide1 {
    display: none;
  }

  .mini1 {
    display: block;
  }

  .classimage {
    background-size: 70px;
  }

  .tricircle {
    background-size: 90px;
  }

  .A {
    margin-top: 4px;
    font-size: 30px;
  }

  .B {
    margin-top: -8px;
    font-size: 20px;
  }

  .C {
    margin-top: -8px;
    margin-bottom: 0;
    font-size: 18px;
  }
}

@media ( max-width: 480px ) {
  .wide2 {
    display: none;
  }

  .mini2 {
    display: block;
  }
}

@media ( max-width: 420px ) {
  .classimage {
    background-size: 60px;
    background-position: center right 2px;
  }

  .tricircle {
    background-size: 70px;
  }
}

@media ( max-width: 380px ) {
  .classimage {
    background-size: 50px;
    background-position: center right -5px;
  }

  .tricircle {
    background-size: 60px;
  }

  .A {
    margin-top: 0px;
    font-size: 25px;
  }

  .B {
    margin-top: -8px;
    font-size: 17px;
  }

  .C {
    margin-top: -8px;
    margin-bottom: 0;
    font-size: 16px;
  }
}

p {
    line-height: 141%;
}

[[/module]]

