[[module CSS]]
/* 使已编辑的东西隐形 */
#header h1 a,
#header h2 span,
#header h2 span::before,
#login-status,
.no {
  color: transparent;
  text-shadow: none;
}
/* 与标准Verdana字体拉丁字母相比，编辑字符有点太宽了，所以弄窄一点 */
.no {
  letter-spacing: -1px;
}

/* 隐藏原始页面标题 */
#page-title {
  display: none;
}

/* 让我的假页面标题看着更像原来的（感谢SCP-2998） */
.page-title {
  border-bottom: solid 1px #bbbbbb;
  color: #990011;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 200%;
}
.page-title p {
  margin: 0;
}

/* 页面顶端的通知 */
.notice {
  border: solid 1px #999999;
  background: #f2f2c2;
  padding: 5px;
  text-align: center;
  margin: 1rem;
}
.raisa.notice {
  background: #f2f2c2;
}

/* 观谬论坛假引用框
 * component:parawatch-forum-post的CSS通常是由
 * theme:creepypasta提供的，这里没有使用 */
.parapost {
  background-color: var(--post-bg-color);
  border: 2px solid var(--post-border-color);
  border-left-width: 4px;
  padding: 0 1em;
  margin-top: -0.5em;
  margin-right: var(--post-base-margin);
  margin-bottom: 1em;
  margin-left: calc(
    var(--nest-level) * var(--post-nest-margin) + var(--post-base-margin)
  );
}

:root {
  --post-bg-color: #f9f9f9;
  --post-border-color: #999;
  --post-nest-margin: 1em;
  --post-base-margin: 3em;
}

.reply0 { margin-top: 1em; border-left-width: 6px; --nest-level: 0; }
.reply1 { --nest-level: 1; }
.reply2 { --nest-level: 2; }
.reply3 { --nest-level: 3; }
.reply4 { --nest-level: 4; }
.reply5 { --nest-level: 5; }

@media (min-width: 480px) and (max-width: 580px) {
  :root {
    --post-nest-margin: 0.5em;
    --post-base-margin: 0.5em;
  }
}

@media (max-width: 479px) {
  :root {
    --post-nest-margin: 1em;
    --post-base-margin: 0em;
  }
}
/* 各角色颜色 */
.parapost {
  --post-bg-color: hsl(var(--char-hue), 33.3%, 97%);
  --post-border-color: hsl(var(--char-hue), 33.3%, 50%);
}
.parapost p:first-child > span:first-child {
  color: var(--post-border-color) !important;
}
.parapost hr {
  background-color: var(--post-border-color);
}
.by-limonova  { --char-hue: 0; }
.by-vershinin { --char-hue: 240; }
.by-petrov    { --char-hue: 300; }
.by-mcfenton  { --char-hue: 120; }
[[/module]]

[[div class="page-title" style="display: block;" id="#page-title"]]
