[[module CSS]]
div.email {
    border: solid 2px #A38800;
    box-shadow: 0 1px 3px rgba(0,0,0,.2);
    padding: 0 1em;
    margin: 1em 3em;
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A2375337-19-9ana/ambrose-logo.png);
    background-size: 64px 64px;
    background-position: 95% .5em;
    background-repeat: no-repeat;
    background-color: #F5F2E4;
    color: #21003C;
}

div.email a {
    color: #A38800;
}

div.email a:visited {
    color: #524400;
}

div.email h5 {
    max-width: 85%;
}

div.email h2 span,
div.email h5 span {
    word-break: unset;
}

@media (max-width: 479px) {
    div.email {
        margin: 1em 0;
    }

    div.email h2 {
        max-width: 80%;
    }
}

@media (min-width: 480px) and (max-width: 580px) {
    div.email {
        margin: 0.5em;
    }

    div.email h2 {
        max-width: 80%;
    }
}
[[/module]]

