[[module CSS]]

:root{
     --titleColor: hsl(0, 0%, 95%);
     --subtitleColor: hsl(60, 62%, 85%);
     --lgurl: url(https://scpsandboxcn.wikidot.com/local--files/ambersight/scp4104.png);
}

 #header::before{
     position: absolute;
     width: 100%;
     height: 100%;
     content: "";

     background-image: var(--lgurl);
     background-position: center top;
     background-repeat: no-repeat;
     background-size: auto 10em;
     margin-top: 0.5em;
     opacity: .46;
}

#header h1 a::before {
    content: var(--header-title, "SCP 基金会");
}

#header h2::before {
    content: var(--header-subtitle, "控制，收容，保护");
}

#header h1 a::before{
     text-shadow: 4px 4px 4px hsla(0, 0%, 0%, .8);
}

 #header h2::before{
     text-shadow: 3px 3px 3px hsla(0, 0%, 0%, .9);
}

 #header h1 a::before{
     font-family: BauhausLTDemi, 'Nanum Gothic', Arial, sans-serif;
     font-size: 115%;
}

 #header h2::before{
     font-family: 'Trebuchet MS', Arial, sans-serif;
}

span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
  content: "[";
}
span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:after,
sup.footnoteref > a.footnoteref:after {
  content: "]";
}

[[/module]]

