[[module css]]
@import url('https://fonts.googleapis.com/css?family=Bahianita|Cabin+Sketch|Miss+Fajardose|Nothing+You+Could+Do|Open+Sans|Reenie+Beanie|Ruthie|Tangerine&display=swap');

div.info-container > div.collapsible-block {
    padding: 0 !important;
    margin: 0 !important;
    box-shadow: none !important;
}

.type1 {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", cursive;
    font-style: normal;
    font-size: 1.2em;

    background-attachment: scroll;
    background: linear-gradient(rgba(220, 240, 240, 0.3), rgba(240, 240, 220, 0.5)), url(http://www.scp-wiki.net/local--files/scp-5862/paperbackground.jpg);
    background-clip: border-box;
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 2px 2px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.type1 p,
.type1 ul {
    line-height: 25px;
    margin: 0;
}

.type2 {

    background-attachment: scroll;
    background: url(http://scp-sandbox-3.wikidot.com/local--files/collab:sqydgraves/divider.png);
    background-clip: border-box;
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;

    filter: opacity(75%);

    border: 0px
    border-radius: 0px;
    padding: 0px 0px;
    margin-bottom: 10px;

}


.type3 {
    display: block;
    overflow: hidden;
    font-family: 'Cabin Sketch', cursive;
    font-style: normal;
    font-size: 90%;

    background-attachment: scroll;
    background: url(http://www.scp-wiki.net/local--files/scp-5862/paperbackground.jpg);
    background-clip: border-box;
    background-color: rgb(219, 199, 171);
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;

    filter: grayscale(90%);

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.type3 p,
.type3 ul {
    line-height: 20px;
    margin: 0;
}

[[/module]]


