[[module CSS]]
:root {
/* VARIABLES */

    --backgroundColor: #0c0c0c;
    --divColor: var(--textColor);
    --textColor: #ffffff;

    --headerTitle: "SCP FOUNDATION";
    --headerSubtitle: "SECURED INTERNAL DATABASE";

    --darkAccent: var(--textColor);
    --accentColor: var(--textColor);

/* Author Label "by" Change */
    --swatch-tertiary-color: var(--textColor);
}

/* HEADER */

#header {
    background-image: none;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background-color: var(--backgroundColor);
    background-image: var(--backgroundColor);
}

div#extra-div-2 {
    height: 23px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: var(--backgroundColor);
}

div#container-wrap {
    background: var(--backgroundColor);
}

#header h1 a {
    color: transparent;
    text-shadow: none;
    font-family: 'Fira Code', monospace;
    margin-left: -95px;
}

#header h1 a::before {
    content: var(--headerTitle);
    color: var(--textColor);
    font-variant: small-caps;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
    font-family: 'Fira Code', monospace;
    margin-left: -95px;
}

#header h2 span::before {
    content: var(--headerSubtitle);
    color: var(--textColor);
    text-transform: uppercase;
}

/* MAIN PAGE ELEMENTS */

::selection {
    background: #ededed;
    color: black;
}

body {
    background-color: var(--backgroundColor);
    color: var(--textColor);
    font-family: 'Fira Code', monospace;
}

hr {
    border-bottom: 1px solid var(--textColor);
    border-top: 1px solid var(--textColor);
    background: transparent;
}

.page-source, tt{ 
    font-family: "Fira Code", monospace;
    font-size: 0.87rem;
}

.code {
    background-color: var(--textColor);
    color: var(--backgroundColor);
    border: none;
    box-shadow: none;
    font-family: 'Fira Code', monospace;
}

 .code pre, .code p, .code {
    font-family: "Fira Code", monospace;
    font-size: 0.87rem;
    color: var(--backgroundColor);
}

h1 {font-size: 225%;}
h2 {font-size: 200%;}
h3 {font-size: 175%;}
h4 {font-size: 150%;}
h5 {font-size: 125%;}
h6 {font-size: 100%;}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Fira Code', monospace;
    font-weight: bold;
    color: var(--textColor);
}

#page-title {
    display: none;
}

blockquote,
div.blockquote,
#toc {
    background-color: var(--divColor);
    border: 1px solid var(--divColor);
    box-shadow: 0px 0px 7px var(--backgroundColor);
}

.scp-image-block .scp-image-caption {
    background-color: var(--divColor);
    border: none;
    color: var(--textColor);
    font-size: 0.84rem;
}

#page-content .wiki-content-table tr th {
    border: solid 1px var(--textColor);
    color: var(--backgroundColor);
    background-color: var(--textColor);
}

#page-content .wiki-content-table tr td {
    background-color: var(--divColor);
    border: solid 1px var(--textColor);
}

a {
    color: var(--accentColor);
}

a:visited {
    color: var(--darkAccent);
}

a:hover {
    background-color: var(--accentColor);
    color: var(--backgroundColor);
    text-decoration: underline;
}

#page-content a:hover::before {
    background-color: var(--accentColor);
    color: var(--backgroundColor);
    content:">";
    text-decoration: underline;
}

.page-tags span {
    border-top: 1px solid var(--textColor);
    margin-top: 0.18rem;
}

/* RATING MODULE */

.page-rate-widget-box {
     border: solid 2px var(--divColor);
     background-color: var(--divColor);
     margin-top: 4px;
     margin-bottom:0.70em;
     margin-right: 1em;
}

.page-rate-widget-box .rate-points {
    background-color: var(--divColor) !important;
    border: none;
    color: var(--textColor) !important;
    text-transform: capitalize;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: var(--divColor);
    border-top: none;
    border-bottom: none;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: var(--accentColor);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: var(--accentColor);
    color: var(--backgroundColor);
}

.page-rate-widget-box .cancel {
    background: transparent;
    background-color: var(--divColor);
    border: none;
}

.page-rate-widget-box .cancel a {
    color: var(--accentColor);
}

.page-rate-widget-box .cancel a:hover {
    background: var(--accentColor);
    color: var(--backgroundColor);
}

.page-rate-widget-box .cancel a:hover::before,
.page-rate-widget-box .rateup a:hover::before,
.page-rate-widget-box .ratedown a:hover::before {
    display: none;
