/* Resources Block */

#side-bar .side-block.resources {
    background-color: var(--divColor);
    background-position-y: -11.6em;
    background-size: 100%;
}

/* Main Side Blocks */

#side-bar .side-block {
    background-color: var(--divColor);
    background-size: 100%;
