[[module CSS]]

@import url('https://fonts.googleapis.com/css?family=Special+Elite');
@import url('https://fonts.googleapis.com/css?family=Courier+Prime');
@import url('https://scp-wiki.wdfiles.com/local--files/component%3Aspirit-theme/telegram3.css');
@import url('https://scp-wiki.wdfiles.com/local--files/component%3Aspirit-theme/Yukarimobile.css');

#header {
  background-image: url(https://scp-wiki.wdfiles.com/local--files/grigori-karpin-s-author-page/Sovietlogowhite)
}

div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "SCP Foundation";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 6px;

}
div#header h2:after {
    content: "    Обезопасить, Удержать, Сохранить";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

.CS-title {
    font-family: 'alpha_echoregular';
}

/* YUI-TABS */
/* This is the parent class for most of the tab module.  */
.yui-navset .yui-content{
    background-color: #F1F0E6;
    padding: 10px 25px 10px 25px;
    border-width: 15px 1px 1px 1px;
    border-color: #291f1f;

}

/* This section affects unselected tabs. */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    font-family: 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
    background-color: transparent;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/component%3Aspirit-theme/yuitabs8.png);
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
}

/* This section affects the currently selected tab. */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    font-family: 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
    background:transparent url(https://scp-wiki.wdfiles.com/local--files/component%3Aspirit-theme/yuitabs7.png) ; /* selected tab background */
    color:#000;
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
}

/* This section affects tabs you are hovering over. */
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    font-family: 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
    background:transparent url(https://scp-wiki.wdfiles.com/local--files/component%3Aspirit-theme/yuitabs7.png) ;
    background-size: cover;
    text-decoration: hover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
}

/* This removes the border around tab images. */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #291f1f;
}
.yui-navset li {
    line-height: normal;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border-color: none;
    border-width: 0;
    bottom: 0;
    left: 0;
    padding: 0.25em 0.75em;
    position: relative;
    right: 0;
    top: -1px;
}
span.redacted {
    color: black;
    background-color: black;
}

.c_content {
    font-family: 'Bahnschrift SemiBold';
    font-style: italic;
    width: 90%;
    line-height: 20px;
    display: inline-block;
    padding-top: 75px;
    padding-left: 20px;
    padding-right: 25px;
    vertical-align: top;
    transition: all .7s;
    background: linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.5)), url("https://topia.wdfiles.com/local--files/offset%3Aoldimey/oldtimeywhimey.jpg") ;
}

.c_content h1, .c_content span {
    font-style: normal;
}

.c_content h1 {
    font-size: 150%;
    font-family: "alpha_echoregular";
    font-weight: normal;
    margin: -0.3em 0 -0.3em 0.3em;
    color: inherit;
}

[[/module]]

