[[module CSS]]
/* == COMMON == */
 :root {
	--bgColor: hsl(360, 6%, 21%);
	--fgColor: hsl(360, 8%, 15%);
}

/* == A/V LOG FORMAT == */
 .logs {
	padding-top: .8rem;

	font-family: var(--mono-font);
}

 .logs hr {
	border-color: var(--accentColorLite);
}

 table td {
	line-height: 1.5;
}

 table.dialog {
	display: block;
	padding-top: 1.2rem;

	border-collapse: collapse;
}

 table.dialog th {
	padding: 1.2rem .8rem 1.2rem 0;

	border-right: thin solid var(--accentColorLite);

	font: 1.05rem var(--header-font);
}

 table.dialog td,
 table.dialog td.active tt {
	opacity: .75;

	font-style: italic;
}

 table.dialog td {
	padding: 1.2rem 0 1.2rem .8rem;

	font-family: var(--mono-font);
}

 table.dialog td.active {
	opacity: 1;

	font: 100%/1.5 var(--body-font);
	font-feature-settings: var(--glyphs);
}

 @media (max-width: 767px) {
	 table.dialog th {
		font-size: .85rem;
	}
}

/* == BLACKLIGHT BOX == */
 .modal,
 .blbf-main > div {
	margin: 1.5rem .5rem;
}

 .modal {
	width: calc(100% - 3rem);
	padding: 0 .75rem;

	color: var(--textColorMid);
	background: var(--fgColor);
	border: thick solid var(--accentColorLite);
}

 .blbf-main > div {
	border-width: thick;

	hyphens: none;
}

 .blbf-main > div::before {
	font-family: var(--header-font);
}

 @media (max-width: 767px) {
	 .blbf-main > div {
		padding-top: 0;
	}
}

/* == COLLAPSIBLES == */
 .collapsible-block-folded a.collapsible-block-link {
	animation: flicker .8s ease-in-out infinite alternate;
}

 @keyframes flicker {
	 0% {
		color: transparent;
		border-bottom-color: transparent;
	}

	 50%, 100% {
		color: var(--accentColor);
		border-bottom-color: var(--accentColor);
	}
}

 a.collapsible-block-link:hover {
	color: var(--alphaColor) !important;
}

 .unlock .collapsible-block-folded,
 .unlock .collapsible-block-unfolded-link {
	width: fit-content;
	margin: 5rem auto;
}

 .unlock .collapsible-block-content {
	margin-top: 5rem;
	margin-bottom: 5rem;
}

 .licensebox .collapsible-block-link {
	animation: 0 !important;
}

/* == DIV BLOCKS == */
 div.blockquote.lightweight {
	box-shadow: none;
}

 @media (min-width: 768px) {
	 div.blockquote.lightweight {
		width: 75%;
		margin: auto;
	}
}

 .blockquote.lightweight.custom {
	width: fit-content;
	margin: 3.5rem auto;

	box-shadow: 5px 5px var(--keyColor);
}

/* == TABLES == */
 .abridged .wiki-content-table tr th {
	margin: 3.5rem auto;
	display: block;

	box-shadow: 5px 5px var(--keyColor);

	font: 1.15rem/1.2 var(--header-font);
}

/* == MISC. == */
 .scp-header {
	font-size: x-large;
}

 .icon-tint {
	filter:
		invert(.3)
		sepia(.5)
		saturate(800%)
		hue-rotate(var(--tint))
		opacity(.8);
}
[[/module]]
