sup { font-weight: bold; }
a.collapsible-block-link { font-weight: bold; }

/* VERY FANCY LOOKING BOXES */
.interview{
    border:solid 2px #d4d4d4;
    background:#F5F5F5;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.addendum{
    border:solid 1px #5e5e5e;
    background:#ffffff;
    width:90%;
    padding:15px 15px;
    margin: 10px auto;
}
.request{   /* USED IN TRANSFER REQUESTS */
