  padding-top: 5px;
  border: solid 4px #a9a9a9;
  border-radius: 16px;
  width: 80%;
}
.rant{
  background: #e2e2e2;
  width: 95%
  border: solid 2px #5e5e5e;
  padding-top: 0px;
  padding-bottom: 0px;
  color: black;
}
.dark{
  border-color: #323232;
  border-style: groove;
  background: #424242;
  color:white;
}

.journal{  /*USED IN SWAINE PERSONAL LOGS */
