[[module CSS]]

/*
 * Snagged from HarryBlanks Inadvertent Neolinguistics, which was gotten from notgulls Siege of Site 19
 */

@import url("https://fonts.googleapis.com/css2?family=PT+Serif&display=swap");
@import url("https://fonts.googleapis.com/css2?family=IBM+Plex+Mono&display=swap");

div.irc-log{
    margin:10px;
    margin-top:20px;
    padding:10px;
    border:3px solid #000000;
    background-color:#333333;
    border-radius:15px;
    font-family:"IBM Plex Mono",monospace;
    color:#FFFFFF;
    font-size: 125%;
}

div.irc-log td{
    border:1px solid #000000;
    font-weight:bold;
}

div.irc-log p{
    line-height:200%;
}

div.irc-log span.userred{
    font-weight:bold;
    letter-spacing:1px;
    background-color:#ffabab;
    color:#050505;
    padding-left:2px;
    border:1px solid #ffabab;
    margin-right:.25em;
}

div.irc-log span.userwhite{
    font-weight:bold;
    letter-spacing:1px;
    background-color:#ffffff;
    color:#050505;
    padding-left:2px;
    border:0px solid #ffabab;
    margin-right:.25em;
}

div.irc-log span.userblue{
    font-weight:bold;
    letter-spacing:1px;
    background-color:#abcdff;
    color:#050505;
    padding-left:2px;
    border:1px solid #abcdff;
    margin-right:.25em;
}

div.irc-log span.useryellow{
    font-weight:bold;
    letter-spacing:1px;
    background-color:#fffcb2;
    color:#050505;
    padding-left:2px;
    border:1px solid #fffcb2;
    margin-right:.25em;
}

div.irc-log span.usergreen{
    font-weight:bold;
    letter-spacing:1px;
    background-color:#afffb1;
    color:#050505;
    padding-left:2px;
    border:1px solid #afffb1;
    margin-right:.25em;
}

div.irc-log::before{
    content:"SCiPNET团队直接通信";
    position:relative;
    top:-1.5em;
    left:-1.5em;
    border:2px solid #050505;
    color:#050505;
    background-color:#5effa6;
    font-family:"PT Serif",serif;
    padding:1px 3px;
    border-radius:3px;
    font-weight:bold;
    letter-spacing:2px;
}

div.inventory-report{
    margin:10px;
    margin-top:20px;
    padding:10px;
    border:2px solid #050505;
    background-color:#eee;
    border-radius:3px;
}

.blockquote {
    color: black;
}

.irc-log a {
    background-color:#5effa6;
}

[[/module]]

