[[module CSS]]
:root {
  --link-color: 16,76,184 !important;
  --link-visited-color: 18,20,150 !important;
}

.decommissioning::before, .ettra::before, .temporal::before, .archive::before, .raisa::before, .overwatch::before {
  content: ' ';
  display: block;
  position: absolute;
  left: -15px;
  top: 30px;
  width: 100%;
  height: 100%;
  border: none; 
  float: center; 
}

.decommissioning::before {
  background: url(https://scp-wiki.wdfiles.com/local--files/scp-6070/Decom.%20Dept.%20Logo.svg) top right/40% no-repeat ;
  opacity: 0.15;
}

.ettra::before {
  background: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/ETTRA_LIGHTLOGO.png) top right/40% no-repeat;
  opacity: 0.55;
}

.temporal::before {
  background: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/DELTA_T_LIGHTLOGO.png) top right/40% no-repeat;
  opacity: 0.55;
}

.archive::before {
  background: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/RAISA_LIGHTLOGO.png) top right/40% no-repeat;
  opacity: 0.55;
}

.overwatch::before {
  background: url(http://scp-wiki.wikidot.com/local--files/theme:basalt/O5_LIGHTLOGO.png) top right/40% no-repeat;
  opacity: 0.55;
}

.raisa::before {
  background: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/RAISA_LIGHTLOGO.png) top right/40% no-repeat;
  opacity: 0.55;
}

.document_content {
  position: relative;
}

div.proceedings {
  background-color: rgba(0,0,0,0);
  border-style: none none none solid;
  border-width: medium;
  padding: 0 0 0 10px;
}

[[/module]]
