[[module CSS]]
.modal .collapsible-block-unfolded-link {
    display: none;
}

.hidden-link {
    color: white;
    border: none !important;
}

.uhoh {
    position: fixed;
    top: 0;
    opacity: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.2);
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 99999;
    animation: spooky-mode 45s linear 20s 1 forwards;
    backdrop-filter: grayscale(0.75);
}

.OHNO {
    opacity: 0;
    background-image: url("http://scp-jp-storage.wikidot.com/local--files/file:5477413-73-e318/thefunnyevillogo.png");
    background-size: cover;
    background-position: center center;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    animation: these-fuckers-again 25s linear 35s 1 forwards;
    pointer-events: none;
}

@keyframes these-fuckers-again {
    0% { opacity: 0; }
    100% { opacity: 1; }
}

@keyframes spooky-mode {
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}


.results {
    background-color:#221800;
    border: 3px solid var(--accentColor);
    color: #CC9F77;
    padding: 15px;
    margin-bottom:10px;
    font-family: monospace;
    font-size: 1.2em;
    text-align: center;
}

