    border: 1px solid #333;
    border-radius: 0;
    box-shadow: none;
}

#top-bar div.open-menu a {
    border: 1px solid #333;
    border-radius: 0;
    box-shadow: none;
}

@media (max-width: 767px) {
    #side-bar:target {
        border: 1px black;
        box-shadow: none;
    }
}

#side-bar .side-block {
    border: 1px solid #333;
    border-radius: 0;
    box-shadow: none;
    background-color: #FDF6D7;
}
#side-bar .side-block.media {
    background-color:#D7EFE7;
}
#side-bar .side-block.resources {
    background-color:#F5D8E0;
}

