[[module CSS]]
/* 汉化注：黑标版式的英文缺少 Italic，详见 https://bhl.scpwikicn.com/fonts/fonts.css */
@import url('https://rsms.me/inter/inter.css');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@500..600&display=swap');
/* 汉化注：原文 Inter 的 opentype 特性不知为何均未生效，此处暂且将其置空以达到相同效果 */
@supports (display:grid) {
    @supports (font-feature-settings:inherit) {
        *, :after, :before {
            font-feature-settings: unset;
        }
    }
}
.number {
    font-feature-settings: "lnum" var(--lnum, 1), "pnum" var(--pnum, 1), "kern" var(--kern, 1), "cpsp" var(--cpsp, 1), "liga" var(--liga, 1), "case" var(--case, 1), "clig" var(--clig, 1), "dlig" var(--dlig, 0), "calt" var(--calt, 1), "aalt" var(--aalt, 0), "ss01" var(--ss01, 1), "ss02" var(--ss02, 0), "ss03" var(--ss03, 1), "ss04" var(--ss04, 1), "zero" var(--zero, 1), "cv11" var(--cv11, 0);
} 
/* 汉化注：双引号调用 Noto Sans SC */
@font-face {
  font-family: 'Quotation';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(https://fonts.gstatic.com/s/notosanssc/v37/k3kCo84MPvpLmixcA63oeAL7Iqp5IZJF9bmaG9_FnYkldv7JjxkkgFsFSSOPMOkySAZ73y9ViAt3acb8NexQ2w.117.woff2) format('woff2');
  unicode-range: U+201c-201d;
}
@font-face {
  font-family: 'Quotation';
  font-style: normal;
  font-weight: 700;
  font-display: swap;
  src: url(https://fonts.gstatic.com/s/notosanssc/v37/k3kCo84MPvpLmixcA63oeAL7Iqp5IZJF9bmaGzjCnYkldv7JjxkkgFsFSSOPMOkySAZ73y9ViAt3acb8NexQ2w.117.woff2) format('woff2');
  unicode-range: U+201c-201d;
}
:root:lang(cn) {
    --body-font: 'Quotation', Inter, "Noto Sans SC", Verdana, "Microsoft YaHei", "微软雅黑", Geneva, sans-serif, STXihei, "华文细黑";
}
/* 汉化注：ACS 框字重调整 */
.item>.lang-cn, .number, .level, .level>.lang-cn, .clearance::before, .class-text {
    font-weight: 600 !important; 
}
.class-category>.lang-cn {
    font-weight: 500 !important;
}

.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon}
[[/module]]
