//一桁の数字を二桁の文字列に変換します．
function set2fig(num) {
	var ret;
	if( num < 10 ) { ret = "0" + num; }
	else { ret = num; }
	return ret;
}

//現在時刻を文字列に変換します．
function getNow() {
	var now = new Date();
	var year = now.getFullYear();
	var mon = now.getMonth()+1; 
	var day = now.getDate();
	var hour = set2fig( now.getHours() );
	var min = set2fig( now.getMinutes() );
	var sec = set2fig( now.getSeconds() );
	if(year<2008){
		year=2008;
	}
	var s = year + "/" + mon + "/" + day + " " + hour + ":" + min + ":" + sec; 
	return s;
}

//現在時刻から0-30までの日数を遡った日付の文字列を生成します．
function randomTime(){
	var dt = new Date();
	dt.setDate(dt.getDate() - Math.random() * 31);
	var year1 = dt.getFullYear();
	var mon1 = dt.getMonth()+1; 
	var day1 = dt.getDate();
	if(year1<2008){
		year1=2008;
	}
	var s1 = year1 + "/" + mon1 + "/" + day1; 
	return s1;
}

//Tableの要素数の番号から，無作為に6つの数字を選択して格納した数列を返します．
function getArray(){
	var i, j, flag;
	var load = new Array();
	var num = new Array();
	for(i = 0; i<scp; i++){
		num[i] = i;
	}
	for (i = 0; i < 6; i++){
		flag = 0;
		do{
			j = Math.floor( Math.random() * scp ) ;
			if( num[j] != -1){
				load[i] = num[j];
				num[j] = -1;
				flag = 1;
			}
		}while( flag == 0 )
	}
	return load;
}

//現在の西暦から入力した数字を引いた数を底として，そこに0-99までのランダムな数字を足して返します．
function randomYear(rate){
	var dt2 = new Date();
	var y = dt2.getFullYear() + Math.random() * 100 - rate;
	return y;
